INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(216,'table','','\\begin{tabular}{|c|l|} \\hline \\multicolumn{1}{|c|}{Number} & \\multicolumn{1}{|c|}{Equation} \\\\ \\hline\\hline 1 & \\sin^2\\theta + \\cos^2\\theta = 1  \\rule[-6pt]{0pt}{18pt} \\\\ \\hline 2 & \\cos 2\\theta =  \\cos^2\\theta - \\sin^2\\theta  \\rule[-6pt]{0pt}{18pt}  \\\\ \\hline 3 & \\sin 2\\theta =2\\sin \\theta \\cos \\theta \\rule[-4pt]{0pt}{15pt} \\\\ \\hline 4 & \\sin(\\alpha\\pm\\beta) = \\sin \\alpha \\cos \\beta \\pm \\cos \\alpha \\sin\\beta  \\rule[-4pt]{0pt}{15pt} \\\\ \\hline 5 &  \\cos (\\alpha \\pm \\beta) = \\cos \\alpha \\cos \\beta \\mp \\sin \\alpha  \\sin \\beta  \\rule[-4pt]{0pt}{15pt} \\\\ \\hline \\end{tabular}',2,3,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(217,'table','tab:identities','<div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{tabular}{|c|l|} \\hline \\multicolumn{1}{|c|}{Number} & \\multicolumn{1}{|c|}{Equation} \\\\ \\hline\\hline 1 & \\sin^2\\theta + \\cos^2\\theta = 1  \\rule[-6pt]{0pt}{18pt} \\\\ \\hline 2 & \\cos 2\\theta =  \\cos^2\\theta - \\sin^2\\theta  \\rule[-6pt]{0pt}{18pt}  \\\\ \\hline 3 & \\sin 2\\theta =2\\sin \\theta \\cos \\theta \\rule[-4pt]{0pt}{15pt} \\\\ \\hline 4 & \\sin(\\alpha\\pm\\beta) = \\sin \\alpha \\cos \\beta \\pm \\cos \\alpha \\sin\\beta  \\rule[-4pt]{0pt}{15pt} \\\\ \\hline 5 &  \\cos (\\alpha \\pm \\beta) = \\cos \\alpha \\cos \\beta \\mp \\sin \\alpha  \\sin \\beta  \\rule[-4pt]{0pt}{15pt} \\\\ \\hline \\end{tabular}"/></div>',2,3,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(218,'equation','none',' \\frac{d \\sin \\theta}{d \\theta}= \\cos \\theta \\quad  \\mbox{and} \\quad  \\frac{d \\cos \\theta}{d \\theta}= -\\sin \\theta ',2,3,10);
