INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(229,'equation','eq:general-cosine','x(t)=A\\cos(\\omega_0t+\\phi)=A\\cos(2\\pi f_0t+\\phi)',2,4,1);
