INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(238,'math',NULL,'-1',2,4,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(239,'math',NULL,'x(t)',2,4,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(240,'math',NULL,'+A',2,4,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(241,'math',NULL,'-A',2,4,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(242,'index',NULL,'Amplitude',2,4,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(243,'paragraph',NULL,'<ol class="ITS_alph"> <li>   <span class="ITS_HTML">A</span> is called the <span class="ITS_blue">amplitude</span>.  The amplitude is a scaling  factor that determines how large the cosine signal will be. Since the function <span class="ITS_HTML">cos&theta;</span> oscillates between <span class="ITS_HTML">+1</span> and <span class="ITS_HTML">-1</span>, the signal <span class="ITS_HTML">x(t)</span> in <a href="eq:general-cosine" class="ITS_reference">&raquo;</a> oscillates between <span class="ITS_HTML">+A</span> and <span class="ITS_HTML">-A</span>.','2','4','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(244,'equation','none',' x(t)=A\\sin(\\omega_0t+\\phi^{\\prime}) =A\\cos(\\omega_0t+\\phi^{\\prime}-\\pi/2) ',2,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(245,'math',NULL,'\\phi',2,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(246,'math',NULL,'x(t)=A\\sin (\\omega_0 t + \\phi^{\\prime})',2,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(247,'math',NULL,'\\phi =\\phi^{\\prime -\\pi/2',2,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(248,'index',NULL,'Phase shift',2,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(249,'paragraph',NULL,'<li>  <span class="ITS_HTML">&phi;</span> is called the <span class="ITS_blue"> phase shift</span>.  The units of phase shift  must be radians, since the argument of the cosine must be in radians. We will generally prefer to use the cosine function when defining the phase shift. If we happen to have a formula containing sine, e.g., <span class="ITS_HTML">x(t)=Asin (&omega;<sub>0</sub> t + &phi;<sup>&prime;</sup>)</span>, then we can rewrite it in terms of cosine if we use the equivalence property in Table <a href="tab:trig-props" class="ITS_reference">&raquo;</a>. The result is: <div class="ITS_EQUATION"> x(t)=Asin(&omega;<sub>0t</sub>+&phi;<sup>&prime;</sup>) =Acos(&omega;<sub>0t</sub>+&phi;<sup>&prime;</sup>-&pi;/2) </div> so we define the phase shift to be <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\phi =\\phi^{\\prime -\\pi/2"/>} in <a href="eq:general-cosine" class="ITS_reference">&raquo;</a>. For simplicity and to prevent confusion, we often avoid using the sine function.','2','4','3');
