INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(260,'index',NULL,'Frequency!cyclic',2,4,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(261,'paragraph',NULL,'<li> <span class="ITS_HTML">&omega;<sub>0</sub></span> is called the <span class="ITS_blue">radian frequency</span>.  Since the  argument of the cosine function must be in radians, which is dimensionless, the quantity <span class="ITS_HTML">&omega;<sub>0</sub> t</span> must likewise be dimensionless. Thus, <span class="ITS_HTML">&omega;<sub>0</sub></span> must have units of rad/sec if <span class="ITS_HTML">t</span> has units of sec. Similarly, <span class="ITS_HTML">f<sub>0</sub>=&omega;<sub>0</sub>/2&pi;</span> is called the <span class="ITS_blue">cyclic frequency</span>, and <span class="ITS_HTML">f<sub>0</sub></span> must have units of sec<span class="ITS_HTML"><sup>-1</sup></span>.   </ol>','2','4','5');
