INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(281,'equation','eq:period-def-f0','\\begin{align} \\omega_0 T_0 = 2\\pi\\quad    &\\Longrightarrow\\quad   T_0 =\\frac{2\\pi}{\\omega_0} \\nonumber \\\\ (2\\pi f_0) T_0 = 2\\pi \\quad    &\\Longrightarrow\\quad   T_0=\\frac{1}{f_0}  \\end{align}',2,4,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(282,'equation','none','\\begin{align*} x(t + T_0 ) &= x(t)\\\\ A\\cos(\\omega_0(t+T_0)+\\phi) &= A\\cos(\\omega_0t+\\phi)\\\\ \\cos(\\omega_0t+\\omega_0T_0+\\phi) &= \\cos(\\omega_0t+\\phi) \\end{align*}',2,4,10);
