INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(313,'footnote',NULL,'Electrical engineers use the abbreviation DC standing for direct current, which is a constant \index{DC',2,4,13);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(314,'paragraph',NULL,'Finally, notice that <span class="ITS_HTML">f<sub>0</sub>=0</span> is a perfectly acceptable value, and when this value is used, the resulting signal is constant (Fig. <a href="fig:freqvar" class="ITS_reference">&raquo;</a>(c)), since <span class="ITS_HTML">5cos (2&pi;&sdot; 0&sdot; t)=5</span> for all values of <span class="ITS_HTML">t</span>. Thus, the constant signal, often called \DC,<sup><a href="#" class="ITS_footnote">[note]</a></sup> current.} is, in fact, a sinusoid of zero frequency.','2','4','13');
