INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(325,'equation','none',' 0 \\leq (t-2) \\leq 2 \\quad  \\Longrightarrow\\quad  2\\leq t \\leq 4. ',2,4,17);
