INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(362,'index',NULL,'Advanced signal',2,4,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(363,'paragraph',NULL,'We will have many occasions to consider time-shifted signals. Whenever a signal can be expressed  in the form <span class="ITS_HTML">x<sub>1</sub>(t) = s(t-t<sub>1</sub>)</span>, we say that <span class="ITS_HTML">x<sub>1</sub>(t)</span> is a time-shifted version of <span class="ITS_HTML">s(t)</span>. If <span class="ITS_HTML">t<sub>1</sub></span> is a positive number, then the shift is to the right, and we say that the signal <span class="ITS_HTML">s(t)</span> has been <span class="ITS_blue">delayed</span> in time.   When <span class="ITS_HTML">t<sub>1</sub></span> is a negative number, then the shift is to the left, and we say that the signal <span class="ITS_HTML">s(t)</span> was <span class="ITS_blue">advanced</span> in time. In summary, time shift is essentially a redefinition of the time origin of the signal. In general, any function of the form <span class="ITS_HTML">s(t-t<sub>1</sub>)</span> has its origin moved to the location <span class="ITS_HTML">t = t<sub>1</sub></span>.','2','4','22');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(364,'equation','eq:ZZZZ-5','\\begin{align*} x_0(t-t_1) = A\\cos\\left(\\omega_0(t-t_1)\\right) &= A\\cos(\\omega_0t+\\phi)\\\\ \\cos( \\omega_0t-\\omega_0t_1)   &= \\cos(\\omega_0 t + \\phi )  \\end{align*}',2,4,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(365,'equation','eq:phase-delay','\\phi =  - 2\\pi f_0 t_1=- 2\\pi \\left(\\frac{t_1}{T_0}\\right)',2,4,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(366,'equation','none',' t_1  = - \\frac{\\phi}{\\omega_0} = - \\frac{\\phi}{2\\pi f_0} ',2,4,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(367,'math',NULL,'t=0',2,4,23);
