INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(672,'paragraph',NULL,'<ol class="ITS_alph"> <li>  Draw a copy of <span class="ITS_HTML">z<sub>1</sub></span> with its tail at the head of <span class="ITS_HTML">z<sub>2</sub></span>. Call this displaced vector <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat z_1"/>.','2','7','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(673,'math',NULL,'\\hat z_1',2,7,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(674,'math',NULL,'z_3',2,7,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(675,'paragraph',NULL,'<li>  Draw the vector from the origin to the head of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat z_1"/>. This is the sum <span class="ITS_HTML">z<sub>3</sub></span>. </ol>','2','7','7');
