INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(679,'image','Chapter02/art/2f14','Graphical construction of complex number addition, $z_3 = z_1 + z_2$, shows that the process is the same as vector addition.',2,7,10);
