INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1096,'equation','eq:synvowel','\\begin{align} x(t) = 2 \\mathb{R} \\big\\{&a_2 e^{ j2\\pi 2f_0 t} + a_4 e^{ j2\\pi 4f_0 t} + a_5 e^{ j2\\pi 5f_0 t}\\\\ &+ a_{16} e^{ j2\\pi 16f_0 t} + a_{17} e^{ j2\\pi 17f_0 t}\\big\\}  \\end{align}',3,4,8);
