INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1431,'math',NULL,'a_k',3,8,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1432,'math',NULL,'k=0',3,8,8);
