INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1468,'math',NULL,'f = 0',3,8,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1469,'paragraph',NULL,'In the synthesis formula <a href="eq:Fourier-Series-synthesis" class="ITS_reference">&raquo;</a>, the <span class="ITS_HTML">a<sub>0</sub></span> coefficient is an additive constant, so a change in its value will move the plot of the signal up or down vertically. The terminology &ldquo;DC&rdquo; comes from electric circuits, where a constant value of current is called direct current, or DC. It is common to call <span class="ITS_HTML">a<sub>0</sub></span>  the DC coefficient, or DC term, in a Fourier expansion. Finally, one should note that the frequency of DC is <span class="ITS_HTML">&fnof; = 0</span>.','3','8','12');
