INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1589,'equation','eq:FS-triangle-wave','a_k=\\left\\{    \\begin{array}{ll} { \\frac{-4}{\\pi^2 k^2}} & \\quad   $k$ an odd integer \\\\ 0 & \\quad   $k$ an even integer. \\end{array} \\right.',3,8,47);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1590,'paragraph',NULL,'Another set of interesting coefficients is obtained by squaring and scaling the complex amplitudes given in <a href="eq:FS-square-wave" class="ITS_reference">&raquo;</a>: <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?a_k=\\left\\{    \\begin{array}{ll} { \\frac{-4}{\\pi^2 k^2}} & \\quad   $k$ an odd integer \\\\ 0 & \\quad   $k$ an even integer. \\end{array} \\right."/></div>','3','8','47');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1591,'equation','eq:triangle_wave','v(t) = \\left \\{ \\begin{array}{ccl} 4t/T_0 -1 &&   0\\leq t < \\frac{1}{2} T_0\\\\ -4t/T_0 + 3 &&   \\frac{1}{2} T_0 \\leq t \\leq   T_0. \\end{array}  \\right .',3,8,49);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1592,'math',NULL,'v(t)',3,8,49);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1593,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>  Show that the triangle wave <span class="ITS_HTML">v(t)</span> can be defined by the following equation over one period: <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?v(t) = \\left \\{ \\begin{array}{ccl} 4t/T_0 -1 &&   0\\leq t < \\frac{1}{2} T_0\\\\ -4t/T_0 + 3 &&   \\frac{1}{2} T_0 \\leq t \\leq   T_0. \\end{array}  \\right ."/></div> </div>','3','8','49');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1594,'equation','none',' s(t) = \\frac{T_0}{4} \\frac{d}{dt}v(t). ',3,8,50);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1595,'equation','none',' \\sum_{k=-\\infty}^{\\infty} a_k\\, e^{ j2\\pi k f_0 t} = \\frac{T_0}{4}\\frac{d}{dt} \\left ( \\sum_{k=-\\infty}^{\\infty} V_k\\, e^{ j2\\pi k f_0 t} \\right ). ',3,8,50);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1596,'equation','none',' \\sum_{k=-\\infty}^{\\infty} a_k\\, e^{ j2\\pi k f_0 t} =  \\sum_{k=-\\infty}^{\\infty} \\frac{T_0}{4} (j2\\pi k f_0) V_k\\, e^{ j2\\pi k f_0 t}. ',3,8,50);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1597,'equation','none',' a_k = \\frac{T_0}{4} (j2\\pi k f_0)  V_k, ',3,8,50);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1598,'equation','none',' V_k = \\frac{4 a_k}{T_0 (j2\\pi k f_0) } = \\left\\{    \\begin{array}{ll} { \\frac{4 }{(j2\\pi k) }\\cdot \\frac{2}{j\\pi k}} & \\quad   $k$ an odd integer \\\\ 0 & \\quad   $k$ an even integer. \\end{array} \\right. ',3,8,50);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(1599,'equation','none',' V_k = \\frac{2????? }{(-\\pi^2 k^2) } \\quad  \\quad \\mbox{for $k$ odd}. ',3,8,50);
