INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2066,'paragraph',NULL,'In summary, the Shannon sampling theorem guarantees that if <span class="ITS_HTML">x(t)</span> contains no frequencies higher than <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?f_\ max"/>, and if <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?f_s>2f_\ max"/>, then the output signal <span class="ITS_HTML">y(t)</span> of the ideal D-to-C converter is <i> equal</i> to the signal <span class="ITS_HTML">x(t)</span>, the input to the ideal C-to-D converter in Fig. <a href="fig:CtoD-then-DtoC" class="ITS_reference">&raquo;</a>.','4','2','43');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2067,'paragraph',NULL,'\iffalse','4','2','45');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2068,'equation','none','\\begin{align*} y[n] = y(n T_s) &=  A\\cos(2\\pi (f_0 + \\ell  f_s) n T_s + \\phi)\\\\ &=  A\\cos(2\\pi f_0 n T_s + 2\\pi \\ell n f_s T_s + \\phi) \\\\ &=  A\\cos(2\\pi f_0 n T_s + 2\\pi \\ell n  + \\phi)\\\\ &=  A\\cos(2\\pi f_0 n T_s + \\phi) \\\\ &=  x[n] \\end{align*}',4,2,46);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2069,'equation','eq:sinewave','x(t)=A\\cos(2\\pi f_0 t + \\phi)',4,2,46);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2070,'equation','eq:sampsine','x[n]=x(n T_s) = A\\cos(2\\pi f_0 n T_s + \\phi)',4,2,46);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2071,'equation','none',' y(t) = A\\cos(2\\pi (f_0+ \\ell  f_s) t + \\phi) ',4,2,46);
