INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2146,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch04/Fig04-07_samdem6T20.png" alt="FILES/SP1Figures/Ch04/Fig04-07_samdem6T20.png"></center><br><div class="ITS_Caption">Sampling a 100 Hz sinusoid at <span class="ITS_HTML">&fnof;<sub>s</sub> = 500</span> samples/sec. The spectrum plot (bottom) shows the aliased spectrum components as well as the positive and negative frequency components of the original sinusoid at <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega = \\pm 0.4\\pi"/> rad. The time-domain plot (middle) shows the samples <span class="ITS_HTML">x[n]</span> as gray dots, the original signal <span class="ITS_HTML">x(t)</span> as a continuous orange line, and the reconstructed signal <span class="ITS_HTML">y(t)</span> as a dashed black line.</span></div></div>','4','3','8');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2147,'equation','none',' \\begin{array}{ll} \\hat\\omega =  0.4\\pi + 2\\pi\\ell &\\quad   \\ell = 0, \\pm 1, \\pm 2, \\ldots\\\\ \\hat\\omega =  -0.4\\pi + 2\\pi\\ell &\\quad   \\ell = 0, \\pm 1, \\pm 2, \\ldots\\\\ \\end{array} ',4,3,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2148,'math',NULL,'x(t)',4,3,10);
