INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2201,'image','Chapter04/art/samdem6T80_5in','Sampling a 100 Hz sinusoid at $\fs = 125$ samples/sec.  The time-domain plot (middle) shows the samples $x[n]$ as gray dots, the original sinusoid $x(t)$ as a continuous orange line, and the reconstructed signal $y(t)$ as a dashed black line. In this case, $y(t)$ is a 25~Hz sinusoid that passes through the same sample points.',4,3,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2202,'equation','none',' \\begin{array}{ll} \\hat\\omega =  1.6\\pi + 2\\pi\\ell &\\quad   \\ell = 0, \\pm 1, \\pm 2, \\ldots\\\\ \\hat\\omega =  -1.6\\pi + 2\\pi\\ell &\\quad   \\ell = 0, \\pm 1, \\pm 2, \\ldots\\\\ \\end{array} ',4,3,20);
