INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2247,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch04/Fig04-10_foldingplot.png" alt="FILES/SP1Figures/Ch04/Fig04-10_foldingplot.png"></center><br><div class="ITS_Caption">Folding of a sinusoid sampled at <span class="ITS_HTML">&fnof;<sub>s</sub> = 2000</span> samples/sec.  The apparent frequency is the lowest frequency of a sinusoid that has exactly the same samples as the input sinusoid.</span></div></div>','4','3','25');
