INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2686,'image','Chapter05/art/5f5','Operation of the <span class="ITS_HTML">M<sup> </sup>th</span> order causal FIR filter showing various positions of the sliding window of <span class="ITS_HTML">M+1</span> points under which the weighted average is calculated. When the input signal <span class="ITS_HTML">x[l]</span> is also finite length (<span class="ITS_HTML">N</span> points), the sliding window will run onto and off of the input data, so the resulting output signal will also have finite length.',5,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2687,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch05/Fig05-05_FIR_sliding_window.png" alt="FILES/SP1Figures/Ch05/Fig05-05_FIR_sliding_window.png"></center><br><div class="ITS_Caption">Operation of the <span class="ITS_HTML">M<sup> </sup>th</span> order causal FIR filter showing various positions of the sliding window of <span class="ITS_HTML">M+1</span> points under which the weighted average is calculated. When the input signal <span class="ITS_HTML">x[l]</span> is also finite length (<span class="ITS_HTML">N</span> points), the sliding window will run onto and off of the input data, so the resulting output signal will also have finite length.</span></div></div>','5','4','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2688,'equation','none','\\begin{align*} y[n] &= \\sum_{k=0}^3 b_k x[n-k]\\\\ &= 3 x[n]  - x[n-1] + 2x[n-2] + x[n-3] \\end{align*}',5,4,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2689,'equation','none',' \\{b_k\\} = \\{ 3,\\,-1,\\,2,\\,1\\} ',5,4,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2690,'math',NULL,'\\{b_k\\}',5,4,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2691,'math',NULL,'\\{b_k\\}',5,4,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2692,'math',NULL,'M = 3',5,4,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2693,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: FIR Filter Coefficients</span><p>   The FIR filter is completely defined once the set of filter coefficients <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\{b_k\\}"/> is known. For example, if the <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\{b_k\\}"/> of a causal filter are <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? \\{b_k\\} = \\{ 3,\\,-1,\\,2,\\,1\\} "/></div> then we have a length-4 filter with <span class="ITS_HTML">M = 3</span>, and <a href="eq:fir-filter-def" class="ITS_reference">&raquo;</a> expands into a 4-point difference equation: <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align*} y[n] &= \\sum_{k=0}^3 b_k x[n-k]\\\\ &= 3 x[n]  - x[n-1] + 2x[n-2] + x[n-3] \\end{align*}"/></div> </div>','5','4','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2694,'math',NULL,'M',5,4,6);
