INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2700,'index',NULL,'Filter length',5,4,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2701,'paragraph',NULL,'The parameter <span class="ITS_HTML">M</span> is the <span class="ITS_blue">order</span> of the FIR filter.   The number of filter coefficients is also called the  filter  <span class="ITS_blue">length</span> <span class="ITS_HTML">(L)</span>. The length is one greater than the order; i.e., <span class="ITS_HTML">L=M\!+\!1</span>. This terminology will make more sense after we have introduced the <span class="ITS_HTML">z</span>-transform in Chapter <a href="chapter:seven-z-transform" class="ITS_reference">&raquo;</a>.','5','4','6');
