INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2791,'paragraph',NULL,'As we have observed, the sum evaluates to a single term for each value of <span class="ITS_HTML">n</span> because each <span class="ITS_HTML">&delta;[n-k]</span> is nonzero only when <span class="ITS_HTML">n-k=0</span> or <span class="ITS_HTML">n=k</span>. In the tabulated form, the impulse response is','5','4','40');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2792,'table','','\\begin{tabular}{|c||||c|c|c|c|c|c|c|c|c|} \\hline n & n<0 & 0 & 1 & 2 & 3 & \\ldots & M & M+1 & n>M\\\\ \\hline\\hline\\hline\\hline x[n]=\\delta[n] & 0 & 1 & 0 & 0 & 0 & 0 & 0 & 0 & 0 \\\\ \\hline y[n]=h[n] &0 & b_0 & b_1 & b_2 & b_3 & \\ldots & b_M & 0 & 0 \\\\ \\hline \\end{tabular}',5,4,41);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2793,'paragraph',NULL,'\small \renewcommand{\arraystretch}{1.25} \renewcommand{\tabcolsep}{2pt} <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{tabular}{|c||||c|c|c|c|c|c|c|c|c|} \\hline n & n<0 & 0 & 1 & 2 & 3 & \\ldots & M & M+1 & n>M\\\\ \\hline\\hline\\hline\\hline x[n]=\\delta[n] & 0 & 1 & 0 & 0 & 0 & 0 & 0 & 0 & 0 \\\\ \\hline y[n]=h[n] &0 & b_0 & b_1 & b_2 & b_3 & \\ldots & b_M & 0 & 0 \\\\ \\hline \\end{tabular}"/></div>','5','4','41');
