INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2799,'index',NULL,'Finite impulse response (FIR) system',5,4,43);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2800,'paragraph',NULL,'In other words, the impulse response <span class="ITS_HTML">h[n]</span> of the FIR filter is simply the sequence of difference equation coefficients. Since <span class="ITS_HTML">h[n] = 0</span> for <span class="ITS_HTML">n<0</span> and for <span class="ITS_HTML">n>M</span>, the length of the impulse response sequence <span class="ITS_HTML">h[n]</span> is finite.  This is why the system <a href="eq:fir-filter-def" class="ITS_reference">&raquo;</a> is called a <span class="ITS_blue">finite impulse response, (FIR)</span> system. Figure <a href="fig:3-point" class="ITS_reference">&raquo;</a> illustrates a plot of the impulse response for the case of the causal 3-point running-average filter.','5','4','43');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(2801,'image','Chapter05/art/5f9','Impulse response of 3-point running average filter, $h[n]$.',5,4,45);
