INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3252,'math',NULL,'\\hat\\omega=\\omega T_s',6,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3253,'math',NULL,'T_s',6,2,1);
