INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3267,'math',NULL,'e^{j\\hat\\omega}',6,2,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3268,'math',NULL,'H(e^{j\\hat\\omega})',6,2,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3269,'math',NULL,'\\hat\\omega',6,2,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3270,'math',NULL,'b_k',6,2,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3271,'math',NULL,'h[k]',6,2,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3272,'index',NULL,'FIR system! frequency response',6,2,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3273,'paragraph',NULL,'However, there is an issue of notation for the frequency response that is dictated by consistency with the \ZT\ (to be introduced in Ch. <a href="chapter:seven-z-transform" class="ITS_reference">&raquo;</a>). In <a href="eq:freqres2" class="ITS_reference">&raquo;</a> the righthand side contains powers of the complex exponential <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?e^{j\\hat\\omega}"/>. This is true of many expressions for the frequency response. Therefore, we elect to use the notation <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega})"/> instead of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?{\\cal H}(\\hat\\omega)"/> to emphasize the ubiquity of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?e^{j\\hat\\omega}"/>. The function <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega})"/> still depends on the variable <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>.  Furthermore, since the impulse response sequence of an FIR filter is the same as the sequence of filter coefficients, we can express the frequency response in terms of either the filter coefficients <span class="ITS_HTML">b<sub>k</sub></span> or the impulse response <span class="ITS_HTML">h[k]</span>; i.e.,','6','2','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3274,'equation','eq:freqres3','\\begin{array}{c} \\mbox{{The frequency response of an FIR system}}\\\\  H(e^{j\\hat\\omega}) = \\sum_{k=0}^M b_ke^{ -j\\hat\\omega k} = \\sum_{k=0}^M h[k] e^{ -j\\hat\\omega k} \\end{array} ',6,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3275,'paragraph',NULL,'<div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi?\\begin{array}{c} \{The frequency response of an FIR system}\\\\  H(e^{j\\hat\\omega}) = \\sum_{k=0}^M b_ke^{ -j\\hat\\omega k} = \\sum_{k=0}^M h[k] e^{ -j\\hat\\omega k} \\end{array} "/></div>','6','2','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3276,'math',NULL,'\\hat\\omega',6,2,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3277,'math',NULL,'y[n]=H(e^{j\\hat\\omega}) x[n]',6,2,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3278,'math',NULL,'\\hat\\omega',6,2,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3279,'math',NULL,'y[n]=H(e^{j\\hat\\omega}) x[n]',6,2,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3280,'math',NULL,'x[n]=Ae^{j\\phi}e^{j\\hat\\omega n}',6,2,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3281,'paragraph',NULL,'Several important points can be made about <a href="eq:freqres1" class="ITS_reference">&raquo;</a> and <a href="eq:freqres3" class="ITS_reference">&raquo;</a>. First of all, the precise interpretation of <a href="eq:freqres1" class="ITS_reference">&raquo;</a> is as follows: When the input is a discrete-time complex exponential signal, the output of an LTI FIR filter is also a discrete-time complex exponential signal with a different complex amplitude, but the same frequency <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>. The frequency response multiplies the signal, thereby changing the complex amplitude. While it is tempting to express this fact by the mathematical statement <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?y[n]=H(e^{j\\hat\\omega}) x[n]"/>, it is strongly recommended that this never be done because it is too easy to forget that the mathematical statement  is true <i> only</i> for complex exponential signals of frequency <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>. The statement <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?y[n]=H(e^{j\\hat\\omega}) x[n]"/> is <i> meaningless</i> for any signal other than signals of precisely the form <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x[n]=Ae^{j\\phi}e^{j\\hat\\omega n}"/>. <i> It is very important to understand this point.</i>','6','2','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3282,'equation','eq:mag-and-angle','\\begin{align} y[n] &= |H(e^{j\\hat\\omega})| e^{ j\\angle H(e^{j\\hat\\omega})} \\cdot A e^{ j\\phi}e^{ j\\hat\\omega n} \\nonumber\\\\ &= (|H(e^{j\\hat\\omega})|  A) \\cdot e^{ j(\\angle H(e^{j\\hat\\omega})+\\phi)}e^{ j\\hat\\omega n}  \\end{align}',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3283,'math',NULL,'H(e^{j\\hat\\omega})',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3284,'math',NULL,'H(e^{j\\hat\\omega})=|H(e^{j\\hat\\omega})|e^{j\\angle H(e^{j\\hat\\omega})}',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3285,'math',NULL,'H(e^{j\\hat\\omega})= \\mathb{R}\\{H(e^{j\\hat\\omega})\\}+j\\mbox{I}\\{H(e^{j\\hat\\omega})\\}',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3286,'math',NULL,'H(e^{j\\hat\\omega})',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3287,'math',NULL,'x[n]=A e^{ j\\phi}e^{ j\\hat\\omega n}',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3288,'math',NULL,'H(e^{j\\hat\\omega})',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3289,'math',NULL,'|H(e^{j\\hat\\omega})|',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3290,'index',NULL,'Gain',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3291,'index',NULL,'Gain of LTI system',6,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3292,'paragraph',NULL,'A second important point is that the frequency response <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega})"/> is complex-valued so it can be expressed either as <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega})=|H(e^{j\\hat\\omega})|e^{j\\angle H(e^{j\\hat\\omega})}"/> or as <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega})= \\mathb{R}\\{H(e^{j\\hat\\omega})\\}+j\I\\{H(e^{j\\hat\\omega})\\}"/>. The effect of the LTI system on the magnitude and phase of the input  complex exponential signal is determined completely by the frequency response function <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega})"/>. Specifically, if the input is <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?x[n]=A e^{ j\\phi}e^{ j\\hat\\omega n}"/>, then using the polar form of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega})"/> gives the result <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align} y[n] &= |H(e^{j\\hat\\omega})| e^{ j\\angle H(e^{j\\hat\\omega})} \\cdot A e^{ j\\phi}e^{ j\\hat\\omega n} \\nonumber\\\\ &= (|H(e^{j\\hat\\omega})|  A) \\cdot e^{ j(\\angle H(e^{j\\hat\\omega})+\\phi)}e^{ j\\hat\\omega n}  \\end{align}"/></div> The magnitude and angle form of the frequency response is the most convenient form, since multiplication is most conveniently accomplished in polar form. The angle of the frequency response simply adds to the phase of the input, thereby producing additional phase shift in the complex exponential signal. Since the magnitude of the frequency response multiplies the magnitude of the complex exponential signal, this part of the frequency response controls the size of the  output.  Thus, <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?|H(e^{j\\hat\\omega})|"/> is also referred to as the <i> gain</i> of the system.','6','2','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3293,'equation','none','\\begin{align*} H(e^{j\\hat\\omega}) &= 1 + 2 e^{ -j\\hat\\omega} + e^{ -j\\hat\\omega 2}\\\\ &= e^{ -j\\hat\\omega} \\left( e^{ j\\hat\\omega} + 2 + e^{ -j\\hat\\omega} \\right )\\\\ &= e^{ -j\\hat\\omega} \\left ( 2 +2\\cos\\hat\\omega \\right ) \\end{align*}',6,2,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3294,'equation','none',' H(e^{j\\hat\\omega})  = 1 + 2 e^{ -j\\hat\\omega} + e^{ -j\\hat\\omega 2} ',6,2,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3295,'math',NULL,'\\{ b_k \\} = \\{ 1,\\,2,\\,1 \\}',6,2,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3296,'math',NULL,'(2 +2\\cos\\hat\\omega) \\geq 0',6,2,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3297,'math',NULL,'-\\pi < \\hat\\omega \\leq \\pi',6,2,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3298,'math',NULL,'|H(e^{j\\hat\\omega})| = (2 + 2\\cos\\hat\\omega)',6,2,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3299,'math',NULL,'\\angle H(e^{j\\hat\\omega}) = -\\hat\\omega',6,2,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3300,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: Frequency Response Formula</span><p>   Consider an LTI system for which the difference equation coefficients are <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\{ b_k \\} = \\{ 1,\\,2,\\,1 \\}"/>. Substituting into <a href="eq:freqres2" class="ITS_reference">&raquo;</a> gives <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? H(e^{j\\hat\\omega})  = 1 + 2 e^{ -j\\hat\\omega} + e^{ -j\\hat\\omega 2} "/></div> To obtain formulas for the magnitude and phase of the frequency response of this FIR filter, we can manipulate the equation as follows: <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align*} H(e^{j\\hat\\omega}) &= 1 + 2 e^{ -j\\hat\\omega} + e^{ -j\\hat\\omega 2}\\\\ &= e^{ -j\\hat\\omega} \\left( e^{ j\\hat\\omega} + 2 + e^{ -j\\hat\\omega} \\right )\\\\ &= e^{ -j\\hat\\omega} \\left ( 2 +2\\cos\\hat\\omega \\right ) \\end{align*}"/></div> Since <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?(2 +2\\cos\\hat\\omega) \\geq 0"/> for frequencies <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?-\\pi < \\hat\\omega \\leq \\pi"/>, the magnitude is <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?|H(e^{j\\hat\\omega})| = (2 + 2\\cos\\hat\\omega)"/> and the phase is <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\angle H(e^{j\\hat\\omega}) = -\\hat\\omega"/>. </div>','6','2','7');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3301,'equation','none','\\begin{align*} y[n] &=  3e^ -j\\pi/3 \\cdot 2e^{ j\\pi/4} e^{ j\\pi n/3}\\\\ &= ( 3\\cdot 2)\\cdot e^{ (j\\pi/4  -j\\pi/3)} e^{ j\\pi n/3} \\\\ &= 6 e^{ -j\\pi/12} e^{ j\\pi n/3} =6 e^{ j\\pi/4} e^{ j\\pi (n-1)/3} \\end{align*}',6,2,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3302,'math',NULL,'x[n]=2e^{j\\pi/4} e^{j\\pi n/3}',6,2,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3303,'math',NULL,'|H(e^{j\\hat\\omega})w{\\pi/3}| = 2+2\\cos(\\pi/3) = { 3}',6,2,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3304,'math',NULL,'\\angle H(e^{j\\hat\\omega}) = { -\\pi/3}',6,2,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3305,'math',NULL,'x[n]',6,2,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3306,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: Complex Exponential Input</span><p>    Consider the complex input <span class="ITS_HTML">x[n]=2e<sup>j&pi;/4</sup> e<sup>j&pi; n/3</sup></span>. If this signal is the input to the system of Example <a href="exam:hamming" class="ITS_reference">&raquo;</a>, then <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?|H(e^{j\\hat\\omega})w{\\pi/3}| = 2+2\\cos(\\pi/3) = { 3}"/> and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\angle H(e^{j\\hat\\omega}) = { -\\pi/3}"/>. Therefore, the output of the system for the given input is <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align*} y[n] &=  3e^ -j\\pi/3 \\cdot 2e^{ j\\pi/4} e^{ j\\pi n/3}\\\\ &= ( 3\\cdot 2)\\cdot e^{ (j\\pi/4  -j\\pi/3)} e^{ j\\pi n/3} \\\\ &= 6 e^{ -j\\pi/12} e^{ j\\pi n/3} =6 e^{ j\\pi/4} e^{ j\\pi (n-1)/3} \\end{align*}"/></div> Thus, for this system and the given input <span class="ITS_HTML">x[n]</span>, the output is equal to the input multiplied by 3, and the phase shift corresponds to a delay of one sample. </div>','6','2','8');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3307,'equation','none',' H(e^{j\\hat\\omega}) = [4 - 4\\cos(\\hat\\omega) + 2\\cos(2\\hat\\omega)]e^{ -j2\\hat\\omega} ',6,2,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3308,'math',NULL,'b_0=b_M',6,2,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3309,'math',NULL,'b_1=b_{M-1}',6,2,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3310,'math',NULL,'\\{ b_k \\} = \\{ 1,\\,-2,\\,4\\,-2,\\,1 \\}',6,2,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3311,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>  When the sequence of coefficients is symmetrical (<span class="ITS_HTML">b<sub>0</sub>=b<sub>M</sub></span>,  <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?b_1=b_{M-1}"/>, etc.), the frequency response can be manipulated as in Example <a href="exam:hamming" class="ITS_reference">&raquo;</a>. Following the style of that example, show that the frequency response of an FIR filter with coefficients <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\{ b_k \\} = \\{ 1,\\,-2,\\,4\\,-2,\\,1 \\}"/> can be expressed as <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? H(e^{j\\hat\\omega}) = [4 - 4\\cos(\\hat\\omega) + 2\\cos(2\\hat\\omega)]e^{ -j2\\hat\\omega} "/></div> </div>','6','2','9');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3312,'section','sec:superposition','Superposition and the Frequency Response',6,3,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3313,'section','sec:superposition','Superposition and the Frequency Response',6,3,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3314,'index',NULL,'Superposition and the frequency response',6,3,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3315,'paragraph',NULL,'<h3 class="ITS_BOOK">6-2  Superposition and the Frequency Response</h3>  The principle of superposition makes it very easy to find the output of a linear time-invariant system if the input is a sum of complex exponential signals. This is why the frequency response is so important in the analysis and design of LTI systems.','6','3','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3316,'equation','none','\\begin{align*} y[n] &= H(e^{j\\hat\\omega})w{0} A_0 e^{ j0 n}\\\\ &  + H(e^{j\\hat\\omega})w{\\hat\\omega_1} \\frac{A_1}{2} e^{ j\\phi_1}e^{ j\\hat\\omega_1 n} + H(e^{j\\hat\\omega})wM{\\hat\\omega_1} \\frac{A_1}{2} e^{ -j\\phi_1}e^{ -j\\hat\\omega_1 n} \\end{align*}',6,3,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3317,'equation','eq:yn-from-Hw1','\\begin{align} y[n] &= H(e^{j\\hat\\omega})w{0} A_0 e^{ j0 n} + H(e^{j\\hat\\omega})w{\\hat\\omega_1} \\frac{A_1}{2} e^{ j\\phi_1}e^{ j\\hat\\omega_1 n} + H(e^{j\\hat\\omega})star{\\om_1}  \\frac{A_1}{2} e^{ -j\\phi_1}e^{ -j\\hat\\omega_1 n}\\\\ &= H(e^{j\\hat\\omega})w{0} A_0 + |H(e^{j\\hat\\omega})w{\\hat\\omega_1}| e^{ j \\angle H(e^{j\\hat\\omega})w{\\hat\\omega_1}}\\frac{A_1}{2} e^{ j\\phi_1}e^{ j\\hat\\omega_1 n} + |H(e^{j\\hat\\omega})w{\\hat\\omega_1}| e^{ -j\\angle H(e^{j\\hat\\omega})w{\\hat\\omega_1}} \\frac{A_1}{2} e^{-j\\phi_1}e^{ -j\\hat\\omega_1 n}\\\\ &= H(e^{j\\hat\\omega})w{0} A_0 + |H(e^{j\\hat\\omega})w{\\hat\\omega_1}| \\frac{A_1}{2} e^{ j(\\hat\\omega_1 n+\\phi_1+\\angle H(e^{j\\hat\\omega})(e^{j\\hat\\omega})w{\\hat\\omega_1})} + |H(e^{j\\hat\\omega})w{\\hat\\omega_1}| \\frac{A_1}{2} e^{ -j(\\hat\\omega_1n+\\phi_1+\\angle H(e^{j\\hat\\omega})w{\\hat\\omega_1})}\\\\ &= H(e^{j\\hat\\omega})w{0} A_0 + |H(e^{j\\hat\\omega})w{\\hat\\omega_1}| A_1 \\cos\\left( \\hat\\omega_1 n + \\phi_1+ \\angle H(e^{j\\hat\\omega})w{\\hat\\omega_1} \\right) \\end{align}',6,3,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3318,'equation','none',' x[n] = A_0 + A_1\\cos(\\hat\\omega_1 n + \\phi_1) ',6,3,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3319,'equation','none',' x[n] = A_0 e^{ j0 n} + \\frac{A_1}{2} e^{ j\\phi_1}e^{ j\\hat\\omega_1 n} + \\frac{A_1}{2} e^{ -j\\phi_1}e^{ -j\\hat\\omega_1 n} ',6,3,2);
