INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3546,'equation','none','\\begin{align*} H(e^{j\\hat\\omega})  &= 1 - e^{ -j\\hat\\omega} \\\\ &= e^{ -j\\hat\\omega/2} \\left(e^{ j\\hat\\omega/2} - e^{ -j\\hat\\omega/2}\\right)\\\\ &= 2 j e^{ -j\\hat\\omega/2} \\sin (\\hat\\omega/2) \\\\ &= 2 \\sin (\\hat\\omega/2)  e^{ j(\\pi/2 - \\hat\\omega/2)} \\end{align*}',6,6,13);
