INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3667,'equation','eq:l-pt-avg-diffeq','\\begin{align} y[n] &= \\frac{1}{L} \\sum_{k=0}^{L-1} x[n-k]  \\\\ &= \\frac{1}{L} \\left( x[n] + x[n-1] +\\dots + x[n-L+1] \\right)\\nonumber LABEL#eq:ZZZZ-128#LABEL \\end{align}',6,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3668,'math',NULL,'L',6,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3669,'math',NULL,'L',6,8,2);
