INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3675,'index',NULL,'<span class="ITS_HTML">L</span>-point running averager',6,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3676,'paragraph',NULL,'A simple linear time-invariant system is defined by the equation <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align} y[n] &= \\frac{1}{L} \\sum_{k=0}^{L-1} x[n-k]  \\\\ &= \\frac{1}{L} \\left( x[n] + x[n-1] +\\dots + x[n-L+1] \\right)\\nonumber  \\end{align}"/></div> This system <a href="eq:l-pt-avg-diffeq" class="ITS_reference">&raquo;</a> is called an  <i> <span class="ITS_HTML">L</span>-point running averager</i>, because the output at time <span class="ITS_HTML">n</span> is computed as the average of <span class="ITS_HTML">x[n]</span> and the <span class="ITS_HTML">L-1</span> previous samples of the input. The system defined by <a href="eq:l-pt-avg-diffeq" class="ITS_reference">&raquo;</a> can be implemented in MATLAB  for <span class="ITS_HTML">L=11</span>  by the statements:\\[1ex] \begin{minipage}{\hsize} {\small% <div class="MATLAB"> bb = ones(11,1)/11<span class="ITS_HTML">1;<br> yy = conv(bb, xx)</span>1;<br> </div>} \end{minipage}\\[1ex]  where  <tt>xx</tt> is a vector containing the samples of the input. The vector  <tt>bb</tt> contains the 11 filter coefficients, which are all the same size, in this case.','6','8','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3677,'equation','eq:closed-form','\\begin{align} H(e^{j\\hat\\omega}) &= \\frac{1}{L} \\sum_{k=0}^{L-1} e^{ -j\\hat\\omega k}\\\\ &= \\frac{1}{L}\\left( \\frac{1-e^{ -j\\hat\\omega L}}{1-e^{ -j\\hat\\omega}}\\right) \\nonumber\\\\ &= \\frac{1}{L}\\left(\\frac{e^{ -j\\hat\\omega L/2}(e^{ j\\hat\\omega L/2} -e^{ -j\\hat\\omega L/2})} {e^{ -j\\hat\\omega /2}(e^{ j\\hat\\omega /2} -e^{ -j\\hat\\omega /2})}\\right)  \\nonumber\\\\ &= \\left(\\frac{\\sin(\\hat\\omega L/2)}{L \\sin(\\hat\\omega /2)}\\right) e^{ -j\\hat\\omega(L-1)/2}  \\end{align}',6,8,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3678,'equation','eq:l-pt-avg-freqresp','H(e^{j\\hat\\omega}) =  \\frac{1}{L} \\sum_{k=0}^{L-1} e^{ -j\\hat\\omega k}',6,8,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3679,'equation','eq:geom-series','\\sum_{k=0}^{L-1} \\alpha^k = \\frac{1-\\alpha^L}{1-\\alpha} \\quad  (\\text{for }\\;\\alpha\\neq 1)',6,8,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3680,'equation','eq:closed-form2','H(e^{j\\hat\\omega}) = D_{L}(e^{j\\hat\\omega}) e^{ -j\\hat\\omega (L-1)/2}',6,8,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3681,'equation','eq:dirichlet-function','D_{L}(e^{j\\hat\\omega}) = \\frac{\\sin(\\hat\\omega L/2)}{L \\sin(\\hat\\omega /2)}',6,8,3);
