INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3799,'math',NULL,'\\angle H(e^{j\\hat\\omega})wM{\\hat\\omega}=-\\angle H(e^{j\\hat\\omega})',6,8,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3800,'paragraph',NULL,'In Fig. <a href="fig:mag-phase" class="ITS_reference">&raquo;</a>, the phase curve is seen to have discontinuities that occur at the zeros of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?D_{11}(e^{j\\hat\\omega})"/>. These discontinuities are due to the combination of multiples of <span class="ITS_HTML">&pi;</span> radians added to the phase due to the negative sign of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?D_{11}(e^{j\\hat\\omega})"/> in the intervals <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?2\\pi/11 < |\\om| <4\\pi/11"/>, <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?6\\pi/11 < |\\om| <8\\pi/11"/>, and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?10\\pi/11 < |\\om| <\\pi"/> and multiples of <span class="ITS_HTML">2&pi;</span> that are added implicitly in the computation of the principal value.  The equation for the phase curve plotted in Fig. <a href="fig:mag-phase" class="ITS_reference">&raquo;</a> is as follows for frequencies <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?0\\leq \\hat\\omega \\leq \\pi"/>: <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? \\angle H(e^{j\\hat\\omega}) = \\begin{cases} -5\\hat\\omega & \\quad   0 \\leq \\hat\\omega < 2\\pi/11\\\\ -5\\hat\\omega + \\pi & \\quad   2\\pi/11 < \\hat\\omega < 4\\pi/11\\\\ -5\\hat\\omega + 2\\pi & \\quad   4\\pi/11 < \\hat\\omega < 6\\pi/11\\\\ -5\\hat\\omega + \\pi + 2\\pi & \\quad   6\\pi/11 < \\hat\\omega < 8\\pi/11\\\\ -5\\hat\\omega + 4\\pi & \\quad   8\\pi/11 < \\hat\\omega < 10\\pi/11\\\\ -5\\hat\\omega + \\pi + 4\\pi & \\quad   10\\pi/11 < \\hat\\omega \\leq \\pi \\end{cases} "/></div> The values for <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?-\\pi < \\hat\\omega <0"/> can be filled in using the fact that <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\angle H(e^{j\\hat\\omega})wM{\\hat\\omega}=-\\angle H(e^{j\\hat\\omega})"/>.','6','8','15');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3801,'math',NULL,'\\angle H(e^{j\\hat\\omega})',6,8,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3802,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>      Test yourself to see whether you understand why the principal value of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\angle H(e^{j\\hat\\omega})"/> is as shown in Fig. <a href="fig:mag-phase" class="ITS_reference">&raquo;</a> for the 11-point moving averager. </div>','6','8','16');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3803,'equation','eq:ZZZZ-126','H_{1}(e^{j\\hat\\omega})=e^{ -j\\hat\\omega (L-1)/2}',6,8,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3804,'equation','eq:ZZZZ-125','H_{2}(e^{j\\hat\\omega}) = D_{L}(e^{j\\hat\\omega}) = \\frac{\\sin(\\hat\\omega L/2)}{L \\sin(\\hat\\omega /2)}',6,8,18);
