INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3825,'math',NULL,'L',6,8,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3826,'math',NULL,'(L-1)/2',6,8,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3827,'paragraph',NULL,'The product representation suggests the block diagram of Fig. <a href="fig:l-pt-avg-blockdiag" class="ITS_reference">&raquo;</a>, which shows that the running averager can be thought of as a cascade combination of a delay followed by a &ldquo;lowpass filter&rdquo;  that accentuates low frequencies relative to high frequencies. The overall moving average system can only be implemented by <a href="eq:l-pt-avg-diffeq" class="ITS_reference">&raquo;</a>. However, the block diagram is a useful convenience for thinking about the system. The system cannot be implemented by this cascade because <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H_{2}(e^{j\\hat\\omega})=D_{L}(e^{j\\hat\\omega})"/> can never be implemented by itself. When <span class="ITS_HTML">(L-1)/2</span> is a integer, <span class="ITS_HTML">w[n]=x[n-(L-1)/2]</span> is a delay in Fig. <a href="fig:l-pt-avg-blockdiag" class="ITS_reference">&raquo;</a>. The case when <span class="ITS_HTML">(L-1)/2</span> is not an integer requires special interpretation, which will be provided in Section <a href="sec:time-delay" class="ITS_reference">&raquo;</a>. For the present discussion, we will assume that <span class="ITS_HTML">L</span> is an odd integer, so that <span class="ITS_HTML">(L-1)/2</span> is also an integer.','6','8','19');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3828,'image','Chapter06/art/6f10','Representation of $L$-point running averager as the cascade of a delay and a real frequency response.',6,8,20);
