INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3849,'math',NULL,'y[n]',6,8,27);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3850,'paragraph',NULL,'An 11-point running averager was applied to <span class="ITS_HTML">x[n]</span>, and the input and output were plotted on the same graph (Fig. <a href="fig:inout-11-pt-avg" class="ITS_reference">&raquo;</a>). Notice that the output <span class="ITS_HTML">y[n]</span> appears to be a smoother version of <span class="ITS_HTML">x[n]</span> with a slight shift to the right. This shift is the 5-sample delay that we expect for an 11-point running averager. The smoothness is a result of the relative attenuation of the higher frequencies in the signal that correspond to the edges of the handwritten characters in the image. To verify the effect of the delay of the system, Fig. <a href="fig:delay-comp" class="ITS_reference">&raquo;</a> shows a plot of <span class="ITS_HTML">w[n]=x[n-5]</span> and <span class="ITS_HTML">y[n]</span>. Now we see that the output appears to be aligned with the input.','6','8','27');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3851,'image','Chapter06/art/6f12','Input and output of 11-point running averager. The solid orange line is the output; the thin gray line is the input.',6,8,28);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3852,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch06/Fig06-12_HW_11avg.png" alt="FILES/SP1Figures/Ch06/Fig06-12_HW_11avg.png"></center><br><div class="ITS_Caption">Input and output of 11-point running averager. The solid orange line is the output; the thin gray line is the input.</span></div></div>','6','8','28');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3853,'image','Chapter06/art/6f13','Delayed (by 5 samples) input and output of 11-point running averager. The solid orange line is the output; the thin gray line is the input.',6,8,29);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3854,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch06/Fig06-13_HW_11shift.png" alt="FILES/SP1Figures/Ch06/Fig06-13_HW_11shift.png"></center><br><div class="ITS_Caption">Delayed (by 5 samples) input and output of 11-point running averager. The solid orange line is the output; the thin gray line is the input.</span></div></div>','6','8','29');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3855,'footnote',NULL,'In MATLAB, the function <code> filter2( )</code> will do this two-dimensional filtering.',6,8,30);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3856,'footnote',NULL,'In MATLAB, the function <code> filter2( )</code> will do this two-dimensional filtering.',6,8,30);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3857,'paragraph',NULL,'The 11-point averager can be applied first over all the rows and then over all the columns of the image to get a visual assessment of the lowpass filtering operation.<sup><a href="#" class="ITS_footnote">[note]</a></sup>  Each row is filtered using the one-dimensional averager, then each column of that filtered image is processed. The result is shown in Fig. <a href="fig:hw-blur" class="ITS_reference">&raquo;</a>, where is it obvious that the lowpass filter has blurred the image. As we have seen, the filter attenuates the high-frequency components of the image.  Thus, we can conclude that sharp edges in an image must be associated with high frequencies.','6','8','30');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3858,'image','Chapter06/art/6f14','Result of filtering both the rows and the columns of the &ldquo;homework&rdquo; image with an 11-point running averager. The processed image had to be rescaled so that its values would occupy the entire gray scale range.',6,8,31);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(3859,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch06/Fig06-14_HW_blur.png" alt="FILES/SP1Figures/Ch06/Fig06-14_HW_blur.png"></center><br><div class="ITS_Caption">Result of filtering both the rows and the columns of the &ldquo;homework&rdquo; image with an 11-point running averager. The processed image had to be rescaled so that its values would occupy the entire gray scale range.</span></div></div>','6','8','31');
