INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4062,'section',NULL,'Definition of the \mbox{\lowercase{<span class="ITS_HTML">z</span>',7,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4063,'section',NULL,'Definition of the \mbox{\lowercase{<span class="ITS_HTML">z</span>',7,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4064,'mbox','sec:z-def','\lowercase{<span class="ITS_HTML">z</span></h3>-Transform',7,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4065,'index',NULL,'Definition of <span class="ITS_HTML">z</span>-transform',7,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4066,'footnote',NULL,'Some authors use positive powers of <span class="ITS_HTML">z</span> in the definition of the \ZT, but this convention is not common in signal processing.',7,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4067,'paragraph',NULL,'<h3 class="ITS_BOOK">7-1  Definition of the \lowercase{<span class="ITS_HTML">z</span></h3>-Transform  A finite-length signal <span class="ITS_HTML">x[n]</span> can be represented by the relation <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?x[n] = \\sum_{k=0}^N x[k]\\delta[n-k] {}"/></div> and the \ZT\ of such a signal is defined by the formula <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?X(z) = \\sum_{k=0}^N x[k]z^{-k} {}"/></div> where we will assume that <span class="ITS_HTML">z</span> represents any complex number; i.e., <span class="ITS_HTML">z</span> is the independent (complex) variable of the \ZT\ <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(z)"/>. Although <a href="eq:z-trans" class="ITS_reference">&raquo;</a> is the conventional definition of the \ZT,<sup><a href="#" class="ITS_footnote">[note]</a></sup>   it is instructive to note that <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(z)"/> can be written in the form <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? X(z) = \\sum_{k=0}^N x[k](z^{-1})^k "/></div> which emphasizes the fact that <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(z)"/> is simply a polynomial of degree <span class="ITS_HTML">N</span> in the variable <span class="ITS_HTML">z<sup>-1</sup></span>.','7','2','1');
