INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4206,'section',NULL,'Properties of the \mbox{\Large\lowercase{<span class="ITS_HTML">z</span>',7,4,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4207,'section',NULL,'Properties of the \mbox{\Large\lowercase{<span class="ITS_HTML">z</span>',7,4,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4208,'mbox','sec:section-7-3','\Large\lowercase{<span class="ITS_HTML">z</span></h3>-Transform',7,4,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4209,'index',NULL,'Properties of<span class="ITS_HTML">z</span>-transform',7,4,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4210,'paragraph',NULL,'<h3 class="ITS_BOOK">7-3  Properties of the \Large\lowercase{<span class="ITS_HTML">z</span></h3>-Transform  In Section <a href="sec:z-def" class="ITS_reference">&raquo;</a> we gave the general definition of the \ZT, and we showed that for finite-length sequences, it is possible to go uniquely back and forth between the sequence and its \ZT. In this sense, we have demonstrated that the \ZT\ is a unique representation of <i> any</i> finite-length sequence (including the impulse response of an FIR filter). In Section <a href="sec:section-7-2" class="ITS_reference">&raquo;</a> we showed that the \ZT\ arises naturally out of the convolution of the impulse-response sequence with a sequence <span class="ITS_HTML">z<sup>n</sup></span>. In this section, we will explore several properties of the \ZT\ representation and indicate how the \ZT\ can be extended to the infinite-length case.','7','4','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4211,'equation','none','\\begin{align*} X(z) &= \\sum_{n=0}^N \\left(ax_1[n]+bx_2[n]\\right) z^{-n} \\\\ &= a\\sum_{n=0}^N x_1[n] z^{-n} + b\\sum_{n=0}^N x_2[n] z^{-n}\\\\ &= a \\Xnz{1} + b \\Xnz{2} \\end{align*}',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4212,'math',NULL,'z',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4213,'math',NULL,'z',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4214,'math',NULL,'x[n]= ax_1[n]+bx_2[n]',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4215,'math',NULL,'x_1[n]',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4216,'math',NULL,'x_2[n]',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4217,'math',NULL,'N',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4218,'math',NULL,'z',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4219,'subsection',NULL,'The Superposition Property of the \mbox{\large\lowercase{<span class="ITS_HTML">z</span>',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4220,'subsection',NULL,'The Superposition Property of the \mbox{\large\lowercase{<span class="ITS_HTML">z</span>',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4221,'mbox','sec:superposition-property','\large\lowercase{<span class="ITS_HTML">z</span></h3>  \index{Superposition property of <span class="ITS_HTML">z</span>-transform',7,4,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4222,'paragraph',NULL,'<h3 class="ITS_BOOK">7-2.1  The Superposition Property of the \large\lowercase{<span class="ITS_HTML">z</span></h3>  \index{Superposition property of <span class="ITS_HTML">z</span>-transform The \ZT\ is a linear transformation. This is easily seen by considering the sequence <span class="ITS_HTML">x[n]= ax<sub>1</sub>[n]+bx<sub>2</sub>[n]</span>, where both <span class="ITS_HTML">x<sub>1</sub>[n]</span> and <span class="ITS_HTML">x<sub>2</sub>[n]</span> are assumed to be finite with length less than or equal to <span class="ITS_HTML">N</span>. Using the definition of <a href="eq:z-trans" class="ITS_reference">&raquo;</a>, we write <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align*} X(z) &= \\sum_{n=0}^N \\left(ax_1[n]+bx_2[n]\\right) z^{-n} \\\\ &= a\\sum_{n=0}^N x_1[n] z^{-n} + b\\sum_{n=0}^N x_2[n] z^{-n}\\\\ &= a \\Xnz{1} + b \\Xnz{2} \\end{align*}"/></div> Thus, we have demonstrated the <i> superposition property</i> for the <span class="ITS_HTML">z</span>-transform:','7','4','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4223,'equation','eq:sup-property',' \\begin{array}{c} \\mbox{{The \\ZT\\ is linear.}}\\\\ ax_1[n]+bx_2[n] \\;\\Zgoes\\; a \\Xnz{2} + b\\Xnz{2} \\end{array} ',7,4,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4224,'paragraph',NULL,'<div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi? \\begin{array}{c} \{The \\ZT\\ is linear.}\\\\ ax_1[n]+bx_2[n] \\;\\Zgoes\\; a \\Xnz{2} + b\\Xnz{2} \\end{array} "/></div>','7','4','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4225,'paragraph',NULL,'This property leads to another way to interpret the \ZT\ of a finite-length sequence, as illustrated in Example <a href="exam:Znum-7-4" class="ITS_reference">&raquo;</a>.','7','4','8');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4226,'equation','eq:sum-of-impulses','x[n] = \\sum_{k=0}^N x[k]\\delta[n-k] { }',7,4,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4227,'equation','eq:t-d-z-d-shifted-impulse-2','\\delta[n-k] \\quad \\Zgoes\\quad  z^{ -k} { }',7,4,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4228,'equation','none',' X(z) = \\sum_{k=0}^N x[k]z^{ -k} ',7,4,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4229,'math',NULL,'x[n]',7,4,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4230,'math',NULL,'z',7,4,9);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4231,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: \ZT\ of a Signal</span><p>   Recall that any finite-length sequence <span class="ITS_HTML">x[n]</span> can be represented as a sum of scaled and shifted impulse sequences as in <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?x[n] = \\sum_{k=0}^N x[k]\\delta[n-k] { }"/></div> Furthermore, recall from <a href="eq:t-d-z-d-shifted-impulse" class="ITS_reference">&raquo;</a> that for a single shifted unit impulse sequence, <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?\\delta[n-k] \\quad \\Zgoes\\quad  z^{ -k} { }"/></div> Thus, applying <a href="eq:t-d-z-d-shifted-impulse-2" class="ITS_reference">&raquo;</a> to each impulse in <a href="eq:sum-of-impulses" class="ITS_reference">&raquo;</a> and then adding the individual <span class="ITS_HTML">z</span>-transforms according to <a href="eq:sup-property" class="ITS_reference">&raquo;</a>, we obtain as before <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? X(z) = \\sum_{k=0}^N x[k]z^{ -k} "/></div> </div>','7','4','9');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4232,'math',NULL,'z',7,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4233,'math',NULL,'z',7,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4234,'math',NULL,'z^{-1}',7,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4235,'math',NULL,'z',7,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4236,'math',NULL,'n',7,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4237,'math',NULL,'x[n]',7,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4238,'subsection',NULL,'The Time-Delay Property of the <span class="ITS_HTML">z</span>-Transform',7,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4239,'subsection',NULL,'The Time-Delay Property of the <span class="ITS_HTML">z</span>-Transform',7,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4240,'index',NULL,'Time-delay property of <span class="ITS_HTML">z</span>-transforms',7,4,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4241,'paragraph',NULL,'<h3 class="ITS_BOOK">7-2.3  The Time-Delay Property of the <span class="ITS_HTML">z</span>-Transform</h3>   Another important property of the \ZT\ is that the quantity <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> in the <span class="ITS_HTML">z</span>-domain corresponds to a time shift of 1 in the <span class="ITS_HTML">n</span>-domain. We will illustrate this property with a numerical example. Consider the length{-}6 signal <span class="ITS_HTML">x[n]</span> defined by the following table of values:','7','4','11');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4242,'table','','\\begin{tabular}{|c||||c|c|c|c|c|c|c|c|} \\hline n & n<0 & 0 & 1 & 2 & 3 & 4 &  5 & n>5 \\\\ \\hline\\hline\\hline\\hline x[n] & 0 & 3 & 1 & 4 & 1 & 5 & 9 & 0 \\\\ \\hline \\end{tabular}',7,4,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4243,'paragraph',NULL,'\renewcommand{\tabcolsep}{4.5pt} \renewcommand{\arraystretch}{1.2} <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{tabular}{|c||||c|c|c|c|c|c|c|c|} \\hline n & n<0 & 0 & 1 & 2 & 3 & 4 &  5 & n>5 \\\\ \\hline\\hline\\hline\\hline x[n] & 0 & 3 & 1 & 4 & 1 & 5 & 9 & 0 \\\\ \\hline \\end{tabular}"/></div>','7','4','12');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4244,'equation','none',' X(z) = 3 + z^{-1} + 4z^{-2} + z^{-3} + 5z^{-4} + 9z^{-5} ',7,4,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4245,'math',NULL,'x[n]',7,4,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4246,'math',NULL,'z^{-1}',7,4,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4247,'math',NULL,'x[n]',7,4,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4248,'math',NULL,'X(z)',7,4,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4249,'math',NULL,'4z^{-2}',7,4,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4250,'math',NULL,'n=2',7,4,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4251,'math',NULL,'x[2]=4.',7,4,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4252,'paragraph',NULL,'The \ZT\  of  <span class="ITS_HTML">x[n]</span> is the  polynomial (in <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/>) <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? X(z) = 3 + z^{-1} + 4z^{-2} + z^{-3} + 5z^{-4} + 9z^{-5} "/></div> Recall that the signal values <span class="ITS_HTML">x[n]</span> are  the coefficients of the polynomial <span class="ITS_HTML">X(z)</span> and that the exponents correspond to the time locations of the values.  For example, the term <span class="ITS_HTML">4z<sup>-2</sup></span> indicates that the signal value at <span class="ITS_HTML">n=2</span> is 4, i.e., <span class="ITS_HTML">x[2]=4.</span>','7','4','14');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4253,'equation','none','\\begin{align*} Y(z) &= z^{-1} X(z)\\\\ &= z^{-1} ( 3 + z^{-1} + 4z^{-2} + z^{-3} + 5z^{-4} + 9z^{-5} )\\\\ &=  0 z^{0} + 3 z^{-1}  + z^{-2} + 4z^{-3} + z^{-4} + 5z^{-5} + 9z^{-6} \\end{align*}',7,4,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4254,'math',NULL,'z^{-1}',7,4,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4255,'math',NULL,'Y(z)',7,4,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4256,'math',NULL,'y[n]',7,4,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4257,'math',NULL,'Y(z)',7,4,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4258,'math',NULL,'y[n]',7,4,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4259,'math',NULL,'y[n]',7,4,15);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4260,'paragraph',NULL,'Now consider the effect of multiplying the  polynomial by <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/>: <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align*} Y(z) &= z^{-1} X(z)\\\\ &= z^{-1} ( 3 + z^{-1} + 4z^{-2} + z^{-3} + 5z^{-4} + 9z^{-5} )\\\\ &=  0 z^{0} + 3 z^{-1}  + z^{-2} + 4z^{-3} + z^{-4} + 5z^{-5} + 9z^{-6} \\end{align*}"/></div> The resulting polynomial <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?Y(z)"/> is the \ZT\ representation of a signal <span class="ITS_HTML">y[n]</span>, which is found by  using the polynomial coefficients  and exponents in <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?Y(z)"/> to determine the values of <span class="ITS_HTML">y[n]</span> at all time positions.   The result is the following table of values for <span class="ITS_HTML">y[n]</span>:','7','4','15');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4261,'table','','\\begin{tabular}{|c||||c|c|c|c|c|c|c|c|c|} \\hline n & n<0 & 0 & 1 & 2 & 3 & 4 &  5 & 6 & n>6 \\\\ \\hline\\hline\\hline\\hline y[n] & 0 & 0 & 3 & 1 & 4 & 1 & 5 & 9 & 0 \\\\ \\hline \\end{tabular}',7,4,16);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4262,'paragraph',NULL,'\renewcommand{\tabcolsep}{4.5pt} \renewcommand{\arraystretch}{1.2} <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{tabular}{|c||||c|c|c|c|c|c|c|c|c|} \\hline n & n<0 & 0 & 1 & 2 & 3 & 4 &  5 & 6 & n>6 \\\\ \\hline\\hline\\hline\\hline y[n] & 0 & 0 & 3 & 1 & 4 & 1 & 5 & 9 & 0 \\\\ \\hline \\end{tabular}"/></div>','7','4','16');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4263,'math',NULL,'y[n]=x[n-1]',7,4,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4264,'math',NULL,'z^{-1}',7,4,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4265,'paragraph',NULL,'Each of the signal samples has moved over one position in the table; i.e., <span class="ITS_HTML">y[n]=x[n-1]</span>. In general, for any finite-length sequence, multiplication of the \ZT\ polynomial by <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> simply subtracts one from each exponent in the polynomial, thereby creating  a delay  of one.  Thus, we have the following fundamental relation:','7','4','17');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4266,'equation','eq:unit-delay-property',' \\begin{array}{c} \\mbox{{A delay of one sample}}\\\\ \\mbox{{ multiplies the \\ZT\\ by z^{-1}.}}\\\\ x[n-1] \\quad   \\Zgoes \\quad   z^{-1} X(z) \\end{array} ',7,4,19);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4267,'paragraph',NULL,'<div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi? \\begin{array}{c} \{A delay of one sample}\\\\ \{ multiplies the \\ZT\\ by z^{-1.}}\\\\ x[n-1] \\quad   \\Zgoes \\quad   z^{-1} X(z) \\end{array} "/></div>','7','4','19');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4268,'math',NULL,'z',7,4,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4269,'paragraph',NULL,'which we will refer to as the <span class="ITS_blue">unit-delay property</span> of the <span class="ITS_HTML">z</span>-transform.','7','4','20');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4270,'math',NULL,'n_0',7,4,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4271,'paragraph',NULL,'The unit-delay property can be generalized for the case of shifting by more than one sample by simply applying <a href="eq:unit-delay-property" class="ITS_reference">&raquo;</a> <span class="ITS_HTML">n<sub>0</sub></span> times.  The general result is','7','4','21');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4272,'equation','eq:general-delay-property',' \\begin{array}{c} \\mbox{{Time delay of n_0 samples}}\\\\ \\mbox{{multiplies the  \\ZT\\ by z^{ -n_0}.}}\\\\ x[n-n_0] \\quad  \\Zgoes \\quad   z^{ -n_0}X(z) \\end{array} ',7,4,23);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4273,'paragraph',NULL,'<div class="ITS_EQUATION"><img class="eqbox" src="/cgi-bin/mimetex.cgi? \\begin{array}{c} \{Time delay of n_0 samples}\\\\ \{multiplies the  \\ZT\\ by z^{ -n_0.}}\\\\ x[n-n_0] \\quad  \\Zgoes \\quad   z^{ -n_0}X(z) \\end{array} "/></div>','7','4','23');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4274,'equation','eq:zt-of-signal','X(z) = \\sum\\limits_{n=0}^{N} x[n] z^{-n} { }',7,4,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4275,'equation','eq:gen-z-trans','X(z) = \\sum\\limits_{n=-\\infty}^{\\infty} x[n] z^{-n} { }',7,4,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4276,'math',NULL,'z',7,4,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4277,'math',NULL,'0\\leq n \\leq N',7,4,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4278,'math',NULL,'+\\infty',7,4,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4279,'math',NULL,'-\\infty',7,4,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4280,'subsection','sec:Bhead-ZZZZ-193','A General <span class="ITS_HTML">z</span>-Transform Formula',7,4,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4281,'subsection','sec:Bhead-ZZZZ-193','A General <span class="ITS_HTML">z</span>-Transform Formula',7,4,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4282,'paragraph',NULL,'<h3 class="ITS_BOOK">7-2.5  A General <span class="ITS_HTML">z</span>-Transform Formula</h3> So far, we have defined the \ZT\ only for finite-length signals. <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?X(z) = \\sum\\limits_{n=0}^{N} x[n] z^{-n} { }"/></div> Our definition assumes that the sequence is nonzero only in the interval <span class="ITS_HTML">0&le; n &le; N</span>. It is possible to extend the definition to signals of infinite length by simply extending the upper or lower limits to <span class="ITS_HTML">+&infin;</span> and <span class="ITS_HTML">-&infin;</span> respectively, i.e., <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?X(z) = \\sum\\limits_{n=-\\infty}^{\\infty} x[n] z^{-n} { }"/></div> However, infinite sums may cause serious mathematical difficulties and require special attention. Summing an infinite number of complex numbers could result in an infinite result. <i> In mathematical terms, the sum might not converge.</i>  Although we will consider the infinite-length case in Chapter <a href="chapter:eight-IIR" class="ITS_reference">&raquo;</a>, the careful mathematical development of a complete \ZT\ theory for signal and system analysis is better left to another, more advanced, course.','7','4','25');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4283,'math',NULL,'z',7,5,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4284,'math',NULL,'z',7,5,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4285,'math',NULL,'z^{-1}',7,5,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4286,'section',NULL,'The \mbox{\lowercase{<span class="ITS_HTML">z</span>',7,5,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4287,'section',NULL,'The \mbox{\lowercase{<span class="ITS_HTML">z</span>',7,5,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4288,'mbox','sec:section-7-4','\lowercase{<span class="ITS_HTML">z</span></h3>-Transform as an Operator',7,5,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4289,'index',NULL,'<span class="ITS_HTML">z</span>-transform as an operator',7,5,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4290,'paragraph',NULL,'<h3 class="ITS_BOOK">7-4  The \lowercase{<span class="ITS_HTML">z</span></h3>-Transform as an Operator  The delay property stated in Section <a href="sec:delay-property" class="ITS_reference">&raquo;</a> suggests that the quantity <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> is in some sense equivalent to a delay or time shift. This point of view leads to a useful, but potentially confusing, interpretation of the \ZT\ as an <i> operator</i>. To see how this interpretation comes about, we will consider the system function of the unit-delay system.','7','5','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4291,'equation','eq:unit-delay-z','\\begin{align} y[n] &= \\it{D} \\{ x[n] \\}\\\\ &= \\it{D} \\{ z^n \\} = z^{n-1} = z^{-1}  z^n  = z^{-1}  x[n] \\end{align}',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4292,'equation','eq:unit-delay-def','y[n] = \\it{D} \\{ x[n] \\} = x[n-1] { }',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4293,'equation','none',' x[n] = z^n \\quad  \\quad  \\mbox{for  all\\ } n ',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4294,'math',NULL,'\\it{D}',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4295,'math',NULL,'z',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4296,'math',NULL,' z^n',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4297,'math',NULL,'z^{-1}',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4298,'math',NULL,'x[n] = z^n',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4299,'subsection','sec:Bhead-ZZZZ-192','Unit-Delay Operator',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4300,'subsection','sec:Bhead-ZZZZ-192','Unit-Delay Operator',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4301,'index',NULL,'Unit-delay operator',7,5,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4302,'paragraph',NULL,'<h3 class="ITS_BOOK">7-3.1  Unit-Delay Operator</h3>  The unit-delay system is one of the basic building blocks for the FIR difference equation.  In the time domain, the unit-delay operator <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\it{D}"/> is defined by <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?y[n] = \\it{D} \\{ x[n] \\} = x[n-1] { }"/></div> It is instructive to find the \ZT\ representation of this system by letting the input to the unit-delay system be the signal <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? x[n] = z^n \\quad  \\quad  \for  all\\  n "/></div> where <span class="ITS_HTML">z</span> is a complex number.  With the <span class="ITS_HTML"> z<sup>n</sup></span> input signal, the output of the unit delay is simply <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\begin{align} y[n] &= \\it{D} \\{ x[n] \\}\\\\ &= \\it{D} \\{ z^n \\} = z^{n-1} = z^{-1}  z^n  = z^{-1}  x[n] \\end{align}"/></div> In other words, the input signal is multiplied by <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/>, <i> in the particular case where</i> <span class="ITS_HTML">x[n] = z<sup>n</sup></span>.','7','5','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4303,'equation','none',' y[n] = z^{-1} \\{ x[n] \\} = x[n-1] ',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4304,'math',NULL,'z^{-1} x[n]',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4305,'math',NULL,'x[n]=z^n',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4306,'math',NULL,'z^{-1}',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4307,'math',NULL,'\\it{D}',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4308,'math',NULL,'x[n]',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4309,'math',NULL,'z^{-1}',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4310,'math',NULL,'z^{-1}',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4311,'math',NULL,'z^{-1}',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4312,'math',NULL,'\\it{D}',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4313,'math',NULL,'z^{-1}',7,5,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4314,'paragraph',NULL,'Strictly speaking, the expression <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1} x[n]"/> in <a href="eq:unit-delay-z" class="ITS_reference">&raquo;</a> is misleading, because we must remember that it holds only for <span class="ITS_HTML">x[n]=z<sup>n</sup></span>. However, it is common to use the quantity <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> interchangeably with the unit-delay operator symbol <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\it{D}"/>, so that we can say that for <i> any</i> input <span class="ITS_HTML">x[n]</span> the action of the unit-delay system is <i> represented</i> by the operator <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/>; i.e., <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? y[n] = z^{-1} \\{ x[n] \\} = x[n-1] "/></div> The brackets enclose the signal operated on by  <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> just as in <a href="eq:unit-delay-def" class="ITS_reference">&raquo;</a>. Thus, if we are careful in our interpretation, we can use the symbol <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> to stand for the delay operator, and many authors use <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\it{D}"/> and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> interchangeably.','7','5','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4315,'math',NULL,'y[n]=x[n-1]',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4316,'math',NULL,'Y(z)=z^{-1} X(z)',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4317,'math',NULL,'z^{-1}',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4318,'math',NULL,'X(z)',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4319,'math',NULL,'Y(z)',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4320,'math',NULL,'z^{-1}',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4321,'math',NULL,'z^{-1}x[n]',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4322,'math',NULL,'x[n]',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4323,'math',NULL,'z',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4324,'math',NULL,'n',7,5,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4325,'paragraph',NULL,'We know from the delay property of Section <a href="sec:delay-property" class="ITS_reference">&raquo;</a> that if <span class="ITS_HTML">y[n]=x[n-1]</span>, then <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?Y(z)=z^{-1} X(z)"/>; i.e., for <i> any</i> finite-length sequence, <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> multiplies <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?X(z)"/> to produce <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?Y(z)"/>. This is the precise way in which <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> represents a unit delay; i.e., it is <i> not</i> appropriate to write <span class="ITS_HTML">z<sup>-1</sup>x[n]</span> without the brackets around <span class="ITS_HTML">x[n]</span>, since this mixes the <span class="ITS_HTML">z</span>-domain and the <span class="ITS_HTML">n</span>-domain.','7','5','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4326,'equation','eq:operator-1st-diff','y[n] \\;=\\; \\left ( 1 - z^{-1} \\right ) \\{ x[n] \\}= x[n] - x[n-1] { }',7,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4327,'equation','none',' y[n] = x[n] - x[n-1] ',7,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4328,'math',NULL,'(1 - z^{-1})',7,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4329,'math',NULL,'x[n]',7,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4330,'math',NULL,'z^{-1}',7,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4331,'math',NULL,'x[n]',7,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4332,'math',NULL,'x[n]',7,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4333,'subsection','sec:Bhead-ZZZZ-191','Operator Notation',7,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4334,'subsection','sec:Bhead-ZZZZ-191','Operator Notation',7,5,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4335,'paragraph',NULL,'<h3 class="ITS_BOOK">7-3.3  Operator Notation</h3> Consider a system that calculates the <span class="ITS_blue">first difference</span> of two successive signal values: <div class="ITS_EQUATION"> y[n] = x[n] - x[n-1] </div> The \ZT\  operator that represents the first-difference system is <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?(1 - z^{-1})"/>, because we can write the &ldquo;operator&rdquo; equation <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?y[n] \\;=\\; \\left ( 1 - z^{-1} \\right ) \\{ x[n] \\}= x[n] - x[n-1] { }"/></div> This equation <a href="eq:operator-1st-diff" class="ITS_reference">&raquo;</a> has the following interpretation: The operator &ldquo;1&rdquo; leaves <span class="ITS_HTML">x[n]</span> unchanged, and the operator <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> delays <span class="ITS_HTML">x[n]</span> before subtracting it from <span class="ITS_HTML">x[n]</span>.','7','5','7');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4336,'equation','none',' y[n] = x[n-n_d] \\quad  \\quad  n_d \\mbox{ is an integer} ',7,5,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4337,'math',NULL,'n_d',7,5,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4338,'math',NULL,'H(z)={ z^{-n_d}}',7,5,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4339,'math',NULL,' z^{-n_d}',7,5,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4340,'paragraph',NULL,'Another simple example would be a system that delays by more than one sample, e.g., by <span class="ITS_HTML">n<sub>d</sub></span> samples: <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? y[n] = x[n-n_d] \\quad  \\quad  n_d \ is an integer "/></div> In this case, the system function is <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)={ z^{-n_d}}"/> and the operator is <span class="ITS_HTML"> z<sup>-n<sub>d</sub></sup></span>, an obvious generalization of the unit-delay case.','7','5','8');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4341,'math',NULL,' x[n] = z^n',7,5,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4342,'math',NULL,'y[n]',7,5,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4343,'math',NULL,'y[n] = H(z) \\{ x[n] \\}',7,5,10);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4344,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>      Derive the   \ZT\ operator for the first-difference system   by working the input  <span class="ITS_HTML"> x[n] = z<sup>n</sup></span> through the system. Write <span class="ITS_HTML">y[n]</span> as <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?y[n] = H(z) \\{ x[n] \\}"/>. </div>','7','5','10');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4345,'math',NULL,'z^{-1}',7,5,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4346,'math',NULL,'z',7,5,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4347,'math',NULL,'z^{-1}',7,5,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4348,'subsection','sec:Bhead-ZZZZ-190','Operator Notation in Block Diagrams',7,5,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4349,'subsection','sec:Bhead-ZZZZ-190','Operator Notation in Block Diagrams',7,5,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4350,'paragraph',NULL,'<h3 class="ITS_BOOK">7-3.5  Operator Notation in Block Diagrams</h3> The delay operator concept <a href="eq:unit-delay-property" class="ITS_reference">&raquo;</a> is particularly useful in block diagrams of LTI systems. In a block diagram representation of the FIR filter, the \ZT\ works as follows: All the unit delays become <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> operators in the transform domain, and, owing to the superposition property of the \ZT, the scalar multipliers and adders are the same as in the time-domain representation. Figure <a href="fig:fir-1st-diff" class="ITS_reference">&raquo;</a> shows the <span class="ITS_HTML">z</span>-domain representation of  a block diagram for a two-point FIR filter&mdash;the <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> operator represents the unit-delay operator.','7','5','11');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4351,'math',NULL,'y[n] = b_0 x[n] + b_1 x[n-1]',7,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4352,'math',NULL,'z^{-1}',7,5,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4353,'paragraph',NULL,'\figurepsAlt{Chapter07/art/7f1}{Chapter07/art/FIR_struct_z}% {Computational structure for a first-order FIR filter whose difference equation is <span class="ITS_HTML">y[n] = b<sub>0</sub> x[n] + b<sub>1</sub> x[n-1]</span>. The block diagram uses <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z^{-1}"/> to denote the unit delay, because they are equivalent.  }','7','5','12');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4354,'math',NULL,'y[n] = \\left ( 1 - z^{-1} \\right ) \\{ x[n] \\}',7,5,13);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4355,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>      Draw a block diagram similar to Fig. <a href="fig:fir-1st-diff" class="ITS_reference">&raquo;</a> for the first difference system: <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?y[n] = \\left ( 1 - z^{-1} \\right ) \\{ x[n] \\}"/> </div>','7','5','13');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4356,'equation','none',' h[n] = \\delta[n-1] ',7,6,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4357,'math',NULL,'z',7,6,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4358,'math',NULL,'z',7,6,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4359,'math',NULL,'n',7,6,1);
