INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4597,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch07/Fig07-03_Hw_viaUC_Hz% {The complex <span class="ITS_HTML">z</span>-plane including the unit circle, where <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z=e^{j\\hat\\omega}"/>. The angle around the circle is determined by the frequency <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>. The orange shading of the unit circle corresponds to a frequency interval from 0 to <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>. The gray dots on the unit circle are the points <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\{ -j,\\,1,\\,j\\,\\"/> which correspond to $\omd = \{-\frac{\pi.png" alt="FILES/SP1Figures/Ch07/Fig07-03_Hw_viaUC_Hz% {The complex <span class="ITS_HTML">z</span>-plane including the unit circle, where <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z=e^{j\\hat\\omega}"/>. The angle around the circle is determined by the frequency <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>. The orange shading of the unit circle corresponds to a frequency interval from 0 to <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>. The gray dots on the unit circle are the points <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\{ -j,\\,1,\\,j\\,\\"/> which correspond to $\omd = \{-\frac{\pi.png"></center><br><div class="ITS_Caption">2},\,0,\,\frac{\pi}{2}\,\}$.</span></div></div>','7','7','10');
