INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4600,'subsection',NULL,'The Zeros and Poles of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/>',7,7,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4601,'index',NULL,'Zeros',7,7,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4602,'index',NULL,'Poles',7,7,12);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4603,'paragraph',NULL,'<h3 class="ITS_BOOK">7-5.3  The Zeros and Poles of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/></h3>     We have already seen that the system function for an FIR system is essentially determined by its zeros. This is illustrated by the following example.','7','7','12');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4604,'equation','eq:pz-example1','\\begin{align} \\!\\!\\!\\!H(z) &= 1-2z^{-1}+2z^{-2}-z^{-3} \\\\ &= (1-z^{-1})(1-e^{j\\pi/3}z^{-1})(1-e^{-j\\pi/3}z^{-1}) LABEL#eq:pz-example2#LABEL \\end{align}',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4605,'equation','eq:pz-example3','\\begin{align} H(z) &= \\frac{ z^3 -2z^2+2z -1}{z^3}  \\\\ &= \\frac{(z-1)(z-e^{j\\pi/3})(z-e^{-j\\pi/3})}{z^3} LABEL#eq:pz-example4#LABEL \\end{align}',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4606,'equation','none',' H(z) = 1-2z^{-1}+2z^{-2}-z^{-3}, ',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4607,'math',NULL,'H(z)',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4608,'math',NULL,'H(z)',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4609,'math',NULL,'z^3/z^3',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4610,'math',NULL,'H(z)',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4611,'math',NULL,'H(z)',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4612,'math',NULL,'z_1=1',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4613,'math',NULL,'z_2=e^{j\\pi/3}',7,7,14);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4614,'math',NULL,'z_3=e^{-j\\pi/3}=z_2^\\ast ',7,7,14);
