INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4633,'math',NULL,'z=a',7,7,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4634,'math',NULL,'z=0',7,7,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4635,'math',NULL,'H(z)',7,7,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4636,'math',NULL,'z',7,7,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4637,'math',NULL,'z',7,7,17);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4638,'paragraph',NULL,'Although it is perhaps less obvious, the locations of both the poles and the zeros are also clear when <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> is written in the form <a href="eq:pz-example2" class="ITS_reference">&raquo;</a>, since each factor of the form <span class="ITS_HTML">(1-az<sup>-1</sup>)</span> always can be expressed as <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? (1-az^{-1}) = \\frac{(z-a)}{z} "/></div> which shows that each factor of the form <span class="ITS_HTML">(1-az<sup>-1</sup>)</span> represents a zero at <span class="ITS_HTML">z=a</span> and a pole at <span class="ITS_HTML">z=0</span>. When <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> contains only negative powers of <span class="ITS_HTML">z</span>, it is usually most convenient to use the representations of the form of <a href="eq:pz-example1" class="ITS_reference">&raquo;</a> and <a href="eq:pz-example2" class="ITS_reference">&raquo;</a> since the negative powers of <span class="ITS_HTML">z</span> have a direct correspondence to the difference equation and the impulse response.','7','7','17');
