INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4649,'index',NULL,' Pole-zero plot',7,7,18);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4650,'paragraph',NULL,'It is useful to display the zeros and poles of  <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> as points in the complex <span class="ITS_HTML">z</span>-plane.  The plot in Fig. <a href="fig:zero-plot" class="ITS_reference">&raquo;</a> shows the three zeros and the three poles for Example <a href="exam:Znum-7-9" class="ITS_reference">&raquo;</a>.  Such a plot is called a <span class="ITS_blue">pole-zero plot</span>. This plot was generated in MATLAB   using the  <tt>zplane</tt> function.<sup><a href="#" class="ITS_footnote">[note]</a></sup> that will make pole-zero plots.} Each zero location is denoted by a small circle, and the three poles at <span class="ITS_HTML">z=0</span> are indicated by a single <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\times"/> with a numeral 3 beside it.  In general, when all the poles are not concentrated at <span class="ITS_HTML">z=0</span>, the <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\times"/> symbol will mark the location of each pole. Since the unit circle is where  <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> is evaluated to obtain the frequency response of the LTI system whose system function is <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/>, it is also shown for reference as a gray circle in Fig. <a href="fig:zero-plot" class="ITS_reference">&raquo;</a>.','7','7','18');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4651,'image','Chapter07/art/7f5','Zeros and poles of $\Hz$ marked in a $z$-plane that includes the unit circle.',7,7,20);
