INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4774,'paragraph',NULL,'\WIDEfigbox{496pt}{0.8}{Chapter07/art/3domainMovie} {One frame of a <i> 3-Domain Movie</i> showing the different domains: time domain <span class="ITS_HTML">(n)</span>, frequency domain <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?(\\hat\\omega)"/>, the \ZT\ domain <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> and a pole-zero plot. In this movie the zero pair moves around on the unit circle.   }','7','7','41');
