INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4778,'section',NULL,'Useful Filters',7,8,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4779,'paragraph',NULL,'<h3 class="ITS_BOOK">7-7  Useful Filters</h3>  Now that we understand the tie between the <span class="ITS_HTML">z</span> and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/> domains, we can exploit that knowledge to design filters with desirable characteristics. In this section, we will look at a special class of bandpass filters (BPFs) that are all close relatives of the running-sum filter.','7','8','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4780,'equation','eq:lpt-running-sum','H(z) = \\sum\\limits_{k=0}^{L-1}  z^{-k} = \\frac{1-z^{-L}}{1-z^{-1}} = \\frac{z^L -1}{z^{L-1}(z-1)} { }',7,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4781,'equation','eq:num-poly','z^L - 1 = 0 \\quad   \\Longrightarrow  z^L=1 { }',7,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4782,'equation','eq:lth-roots-of-unity','z =  e^{j 2\\pi k/L}  \\quad \\mbox{for } k=0,1,2,\\dots,L-1 { }',7,8,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4783,'equation','eq:lpf-l-1-factors','H(z) = \\sum\\limits_{k=0}^{L-1}  z^{-k} = \\prod\\limits_{k=1}^{L-1} (1 - e^{j 2\\pi k /L}z^{-1} ) { }',7,8,2);
