INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4940,'table','tab:hn-24pt-FIR','{\jot=3pt \vspace*{-3ex} <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align*} b_0 = &\\      {-}0.0108 =b_{23}\\\\ b_1 = &\\    \\mbox{\\phantom{#@#-#@#}}   0.0037 =b_{22}\\\\ b_2 = &\\      {-}0.0052 =b_{21}\\\\ b_3 = &\\    \\mbox{\\phantom{#@#-#@#}}   0.0300 =b_{20}\\\\ b_4 = &\\    \\mbox{\\phantom{#@#-#@#}}   0.0000 =b_{19}\\\\ b_5 = &\\      {-}0.0526 =b_{18}\\\\ b_6 = &\\    \\mbox{\\phantom{#@#-#@#}}   0.0164 =b_{17}\\\\ b_7 = &\\      {-}0.0219 =b_{16}\\\\ b_8 = &\\    \\mbox{\\phantom{#@#-#@#}}   0.1275 =b_{15}\\\\ b_9 = &\\    \\mbox{\\phantom{#@#-#@#}}   0.0000 =b_{14}\\\\ b_{10} = &\\   {-}0.3236 =b_{13}\\\\ b_{11} = &\\ \\phantom{-}   0.2330 =b_{12} \\end{align*}"/></div>}',7,9,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4941,'equation','none',' y[n]=\\sum\\limits_{k=0}^{23} b_k x[n-k] ',7,9,6);
