INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4983,'math',NULL,'b_k=b_{M-k}',7,10,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(4984,'math',NULL,'k=0,1,\\dots,M',7,10,3);
