INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5506,'math',NULL,'-6 \\leq \\hat\\omega \\leq +6',8,6,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5507,'math',NULL,'2\\pi',8,6,5);
