INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5543,'subsection','sec:Bhead-ZZZZ-244','Three-Dimensional Plot of a System Function',8,6,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5544,'subsection','sec:Bhead-ZZZZ-244','Three-Dimensional Plot of a System Function',8,6,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5545,'paragraph',NULL,'<h3 class="ITS_BOOK">8-4.3  Three-Dimensional Plot of a System Function</h3>% The relationship between <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega})"/> and the pole-zero locations of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> can be illustrated by making a three-dimensional plot of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> and then cutting out the frequency response. The frequency response <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega})"/> is obtained by selecting the values of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> along the unit circle, i.e., as <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/> goes from <span class="ITS_HTML">-&pi;</span> to <span class="ITS_HTML">+&pi;</span>, the equation <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi? z = e^{j\\hat\\omega}"/> defines the unit circle.','8','6','7');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5546,'equation','none',' H(z) = \\frac{1}{1 - 0.8 z^{-1}} ',8,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5547,'math',NULL,'H(z)',8,6,8);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5548,'math',NULL,'[-1.4,1.4] \\times [-1.4,1.4]',8,6,8);
