INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5573,'image','Chapter08/art/8F10','\ZT\ evaluated over a region of the <span class="ITS_HTML">z</span>-plane including the unit circle. Values along the unit circle are shown as a dark line where the frequency response (magnitude) is evaluated. The view is from the fourth quadrant, so the point <span class="ITS_HTML">z=1</span> is on the right. The first-order filter has a pole  at <span class="ITS_HTML">z = 0.8</span> and  a zero at <span class="ITS_HTML">z = 0</span>.',8,6,11);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5574,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch08/Fig08-01_8F10.png" alt="FILES/SP1Figures/Ch08/Fig08-01_8F10.png"></center><br><div class="ITS_Caption">\ZT\ evaluated over a region of the <span class="ITS_HTML">z</span>-plane including the unit circle. Values along the unit circle are shown as a dark line where the frequency response (magnitude) is evaluated. The view is from the fourth quadrant, so the point <span class="ITS_HTML">z=1</span> is on the right. The first-order filter has a pole  at <span class="ITS_HTML">z = 0.8</span> and  a zero at <span class="ITS_HTML">z = 0</span>.</span></div></div>','8','6','11');
