INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5585,'equation','none','\\begin{align*} y[n] &= a_1 y[n-1] + a_2 y[n-2]\\\\ &\\quad  \\phantom{X}+ b_0 x[n]  + b_1 x[n-1]  + b_2 x[n-2] \\end{align*}',8,7,1);
