INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5906,'math',NULL,'p_2',8,10,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5907,'math',NULL,'p_k^n',8,10,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5908,'subsubsection','sec:Chead-ZZZZ-251','Real Poles',8,10,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5909,'subsubsection','sec:Chead-ZZZZ-251','Real Poles',8,10,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5910,'paragraph',NULL,'<h3 class="ITS_BOOK">8-8.9.1  Real Poles</h3>% If <span class="ITS_HTML">p<sub>1</sub></span> and <span class="ITS_HTML">p<sub>2</sub></span> are real, the impulse response is composed of two real exponentials of the form <span class="ITS_HTML">p<sub>k</sub><sup>n</sup></span>. This case is illustrated by the following example:','8','10','25');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5911,'equation','eq:2nd-order-hz-example','\\begin{align} H(z) &= \\frac{1}{1 - \\mbox{$\\frac{5}{6}$}z^{-1} + \\frac{1}{6} z^{-2}}\\\\ &= \\frac{1}{(1- \\frac{1}{2} z^{-1})(1-\\frac{1}{3} z^{-1})} \\end{align} { }',8,10,26);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5912,'equation','eq:2nd-order-ex-4','y[n] = \\mbox{$\\frac{5}{6}$} y[n-1] - \\frac{1}{6} y[n-2] + x[n]',8,10,26);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5913,'equation','eq:2nd-order-imp-resp-de','h[n] = \\mbox{$\\frac{5}{6}$} h[n-1] - \\frac{1}{6} h[n-2] + \\delta[n] { }',8,10,26);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5914,'math',NULL,'z=\\frac{1}{2}',8,10,26);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5915,'math',NULL,'z=\\frac{1}{3}',8,10,26);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5916,'math',NULL,'z=0',8,10,26);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5917,'math',NULL,'H(z)',8,10,26);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5918,'math',NULL,'H(z)',8,10,26);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5919,'paragraph',NULL,'<div class="ITS_EXAMPLE"><span class="ITS_EXAMPLE_TITLE">EXAMPLE: Second-Order: Real Poles</span><p>%Assume that <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?\\begin{align} H(z) &= \\frac{1}{1 - \$\\frac{5{6}$}z^{-1} + \\frac{1}{6} z^{-2}}\\\\ &= \\frac{1}{(1- \\frac{1}{2} z^{-1})(1-\\frac{1}{3} z^{-1})} \\end{align} { }"/></div> from which we see that the poles are at <span class="ITS_HTML">z=&frac12;</span> and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z=\\frac{1}{3}"/> and that there are two zeros at <span class="ITS_HTML">z=0</span>. The poles and zeros of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> are plotted in Fig. <a href="fig:pz-plot-1" class="ITS_reference">&raquo;</a>. We can extract the filter coefficients from <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> and write the following difference equation <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?y[n] = \$\\frac{5{6}$} y[n-1] - \\frac{1}{6} y[n-2] + x[n]"/></div> which must be satisfied for any input and its corresponding output. Specifically, the impulse response would satisfy the difference equation <div class="ITS_EQUATION"><img class="equation" src="/cgi-bin/mimetex.cgi?h[n] = \$\\frac{5{6}$} h[n-1] - \\frac{1}{6} h[n-2] + \\delta[n] { }"/></div>','8','10','26');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5920,'math',NULL,'z=\\frac{1}{2}',8,10,27);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5921,'math',NULL,'z=\\frac{1}{3}',8,10,27);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5922,'math',NULL,'z=0',8,10,27);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(5923,'paragraph',NULL,'\figurepsAlt{Chapter08/art/8f18}{Chapter08/art/PZ_real2}% {Pole-zero plot for system of Example <a href="exam:two-real-example" class="ITS_reference">&raquo;</a>. The poles are at <span class="ITS_HTML">z=&frac12;</span> and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?z=\\frac{1}{3}"/>; there are two zeros at <span class="ITS_HTML">z=0</span>.  }','8','10','27');
