INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6117,'paragraph',NULL,'Figure <a href="fig:two-pole-mesh" class="ITS_reference">&raquo;</a> shows a plot of the system function <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> at points inside, outside, and on the unit circle. The peaks located at the poles, <span class="ITS_HTML">0.85e<sup>  &plusmn; j  &pi;/2</sup></span>, determine the frequency response behavior near <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega = \\pm  \\pi/2"/>. If the poles were moved closer to the unit circle, the frequency response would have a higher and narrower peak. The zeros at <span class="ITS_HTML">z = &plusmn; 1</span> create valleys that lie on the unit circle at <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega = 0, \\pi"/>. <h3 class="ITS_NOTE"><b>DEMO:</b> Z to Freq</h3>','8','11','19');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6118,'equation','eq:hw-as-vectors','|H(e^{j\\hat\\omega}) | = G\\quad \\frac{|e^{j\\hat\\omega}-z_1|\\quad |e^{j\\hat\\omega}-z_2|} {|e^{j\\hat\\omega}-p_1|\\quad |e^{j\\hat\\omega}-p_2|}',8,11,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6119,'equation','none',' H(z) = G\\,\\frac{(z-z_1)(z-z_2)}{(z-p_1)(z-p_2)} ',8,11,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6120,'equation','none',' |H(e^{j\\hat\\omega}) | = G \\frac{\\overline{Z_1Z}\\cdot\\overline{Z_2Z}} {\\overline{P_1Z}\\cdot\\overline{P_2Z}} ',8,11,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6121,'math',NULL,'|H(e^{j\\hat\\omega}) |',8,11,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6122,'math',NULL,'H(z)',8,11,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6123,'math',NULL,'z_1',8,11,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6124,'math',NULL,'z_2',8,11,20);
