INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6126,'math',NULL,'p_2',8,11,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6127,'math',NULL,'G',8,11,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6128,'math',NULL,'|e^{j\\hat\\omega}-z_i|',8,11,20);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6129,'math',NULL,'|e^{j\\hat\\omega}-p_i|',8,11,20);
