INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6141,'math',NULL,'(z = e^{j\\hat\\omega})',8,11,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6142,'math',NULL,'\\overline{Z_jZ}',8,11,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6143,'math',NULL,'j^{\\text th}',8,11,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6144,'math',NULL,'(z = e^{j\\hat\\omega})',8,11,21);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6145,'paragraph',NULL,'\figurepsAlt{Chapter08/art/8f24}{Chapter08/art/Hw_PZ_vectors}% {\ZT\ evaluated on the unit circle <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?(z = e^{j\\hat\\omega})"/> by using a product of vector lengths from the poles and zeros. {\boldmath<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\overline{P_iZ}"/>} denotes the vector from the <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?i^{\\text th}"/> pole to <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?(z = e^{j\\hat\\omega})"/>, and {\boldmath<img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\overline{Z_jZ}"/>} denotes the vector from the <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?j^{\\text th}"/> zero to <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?(z = e^{j\\hat\\omega})"/>,  }','8','11','21');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6146,'equation','none','\\begin{align*} |\\Hej{\\pi/2} | &= | H(j) |\\\\ &= \\frac{|j-1| \\,|j+1|}{|j - 0.85j|\\,|j -(-0.85j)|}\\\\ &= \\frac{2}{0.15 \\times 1.85}  = 7.207 \\end{align*}',8,11,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6147,'math',NULL,'H(e^{j\\hat\\omega}) ',8,11,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6148,'math',NULL,'\\hat\\omega = \\pi/2',8,11,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6149,'math',NULL,'z =  e^{j\\pi/2}',8,11,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6150,'math',NULL,'z = j',8,11,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6151,'math',NULL,'G',8,11,22);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6152,'paragraph',NULL,'We can apply this geometric reasoning to   estimate the magnitude of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(e^{j\\hat\\omega}) "/> at <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega = \\pi/2"/> in Fig. <a href="fig:two-pole-mesh" class="ITS_reference">&raquo;</a>. We begin by   estimating the lengths of the vectors from the zeros and poles to the point <span class="ITS_HTML">z =  e<sup>j&pi;/2</sup></span>, which is the same as <span class="ITS_HTML">z = j</span>. The lengths of the vectors from the zeros are then divided by the lengths of the vectors from the poles, so we get <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{align*} |\\Hej{\\pi/2} | &= | H(j) |\\\\ &= \\frac{|j-1| \\,|j+1|}{|j - 0.85j|\\,|j -(-0.85j)|}\\\\ &= \\frac{2}{0.15 \\times 1.85}  = 7.207 \\end{align*}"/></div> The gain <span class="ITS_HTML">G</span> was assumed to be 1.','8','11','22');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6153,'paragraph',NULL,'An excellent way to practice with these ideas is to use the MATLAB  GUI called <tt>PeZ</tt> (Fig. <a href="fig:PEZdemoGUI" class="ITS_reference">&raquo;</a>).','8','11','24');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6154,'image','Chapter08/art/PEZdemoGUI','Graphical User Interface (GUI) for <tt>PeZ</tt> which illustrates the <i> three-domains</i> concept. The user can enter poles and zeros and see the resulting impulse response and frequency response. In addition, the poles/zero can be moved with a mouse pointer and and the other plots will updated in real-time.',8,11,25);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6155,'paragraph',NULL,'<div class="ITS_Image"><center><img src="FILES/SP1Figures/Ch08/Fig08-07_PEZdemoGUI.png" alt="FILES/SP1Figures/Ch08/Fig08-07_PEZdemoGUI.png"></center><br><div class="ITS_Caption">Graphical User Interface (GUI) for <tt>PeZ</tt> which illustrates the <i> three-domains</i> concept. The user can enter poles and zeros and see the resulting impulse response and frequency response. In addition, the poles/zero can be moved with a mouse pointer and and the other plots will updated in real-time.</span></div></div>','8','11','25');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6156,'equation','eq:dirform','\\begin{align} H(z) &= \\frac{0.0798 (1\\!+\\!z^{-1}\\!+\\!z^{-2}\\!+\\!z^{-3})} {1\\!-\\!1.556z^{-1}\\!+\\!1.272z^{-2}\\!-\\!0.398z^{-3}}  \\\\ &= \\frac{0.0798(1\\!+\\!z^{-1})(1\\!-\\!e^{ j\\pi/2}z^{-1})(1\\!-\\!e^{-j\\pi/2}z^{-1})} {(1\\!-\\!0.556z^{-1})(1\\!-\\!0.846e^{j0.3\\pi}z^{-1})(1\\!-\\!0.846e^{-j0.3\\pi}z^{-1})} LABEL#eq:factoredform#LABEL \\\\ &= -\\frac{1}{5}+\\frac{0.62}{1\\!-\\!.556z^{-1}}+ \\frac{0.17e^{j0.96\\pi}}{1\\!-\\!.846e^{j0.3\\pi}z^{-1}}+ \\frac{0.17e^{-j0.96\\pi}}{1\\!-\\!.846e^{-j0.3\\pi}z^{-1}} LABEL#eq:fractionform#LABEL \\end{align}',8,12,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6157,'math',NULL,'H(z)',8,12,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6158,'equation','none',' <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\\\begin{align',8,12,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6159,'section','sec:Ahead-ZZZZ-232','Example of an IIR Lowpass Filter',8,12,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6160,'footnote',NULL,'Factoring polynomials and obtaining the partial fraction expansion was done in MATLAB  using the functions <code> roots</code> and <code> residuez</code>, respectively.',8,12,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6161,'section','sec:Ahead-ZZZZ-232','Example of an IIR Lowpass Filter',8,12,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6162,'footnote',NULL,'Factoring polynomials and obtaining the partial fraction expansion was done in MATLAB  using the functions <code> roots</code> and <code> residuez</code>, respectively.',8,12,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6163,'paragraph',NULL,'<h3 class="ITS_BOOK">8-11  Example of an IIR Lowpass Filter</h3> First-order and second-order IIR filters are useful and provide simple examples, but, in many cases, we use higher-order IIR filters because they can realize frequency responses with flatter passbands and stopbands and sharper transition regions.  The  <tt>butter</tt>, <tt>cheby1</tt>,  <tt>cheby2</tt>, and  <tt>ellip</tt> functions in MATLAB&lsquo;s <i> Signal Processing Toolbox</i> can be used to design filters with prescribed frequency-selective characteristics. As an example, consider the system with system function <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> given by <a href="eq:dirform" class="ITS_reference">&raquo;</a>. <div class="ITS_EQUATION"><img class="WideEqFlt" src="/cgi-bin/mimetex.cgi? <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\\\begin{align"/></div> H(z) &= \\frac{0.0798 (1\\!+\\!z^{-1}\\!+\\!z^{-2}\\!+\\!z^{-3})} {1\\!-\\!1.556z^{-1}\\!+\\!1.272z^{-2}\\!-\\!0.398z^{-3}}  \\\\ &= \\frac{0.0798(1\\!+\\!z^{-1})(1\\!-\\!e^{ j\\pi/2}z^{-1})(1\\!-\\!e^{-j\\pi/2}z^{-1})} {(1\\!-\\!0.556z^{-1})(1\\!-\\!0.846e^{j0.3\\pi}z^{-1})(1\\!-\\!0.846e^{-j0.3\\pi}z^{-1})}  \\\\ &= -\\frac{1}{5}+\\frac{0.62}{1\\!-\\!.556z^{-1}}+ \\frac{0.17e^{j0.96\\pi}}{1\\!-\\!.846e^{j0.3\\pi}z^{-1}}+ \\frac{0.17e^{-j0.96\\pi}}{1\\!-\\!.846e^{-j0.3\\pi}z^{-1}}  \\end{align}"/></div> } This system is an example of a lowpass <span class="ITS_blue">elliptic filter</span> whose numerator and denominator coefficients were obtained using the MATLAB  function  <tt>ellip</tt>. The exact call was <tt>ellip(3,1,30,0.3).</tt> Each of the three different forms above is useful: <a href="eq:dirform" class="ITS_reference">&raquo;</a> for identifying the filter coefficients, <a href="eq:factoredform" class="ITS_reference">&raquo;</a> for sketching the pole-zero plot and the frequency response, and <a href="eq:fractionform" class="ITS_reference">&raquo;</a> for finding the impulse response.<sup><a href="#" class="ITS_footnote">[note]</a></sup> Figure <a href="fig:iirexample-2" class="ITS_reference">&raquo;</a> shows the poles and zeros of this filter. Note that all the zeros are on the unit circle and that the poles are strictly inside the unit circle, as they must be for a stable system.','8','12','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6164,'paragraph',NULL,'\figurepsAlt{Chapter08/art/8f25}{Chapter08/art/PZ_ellip}% {Pole-zero plot for a third-order IIR filter <a href="eq:factoredform" class="ITS_reference">&raquo;</a>.  }','8','12','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6165,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>      From <a href="eq:dirform" class="ITS_reference">&raquo;</a> determine the difference equation (Direct Form I) for implementing this system. </div>','8','12','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6166,'math',NULL,'|\\hat\\omega|\\leq 2\\pi(0.15)',8,12,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6167,'math',NULL,'\\pm 0.3\\pi',8,12,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6168,'math',NULL,'\\hat\\omega=\\pm 0.5\\pi',8,12,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6169,'math',NULL,'\\hat\\omega =\\pi',8,12,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6170,'math',NULL,'H(z)',8,12,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6171,'paragraph',NULL,'The system function was evaluated on the unit circle using the MATLAB  function <tt>freqz</tt>. A plot of this result is shown in Fig. <a href="fig:iirexample-1" class="ITS_reference">&raquo;</a>. Note that the frequency response is large in the vicinity of the poles and small around the zeros. In particular, the passband of the frequency response is  <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?|\\hat\\omega|\\leq 2\\pi(0.15)"/>, which corresponds to the poles with angles at <span class="ITS_HTML">&plusmn; 0.3&pi;</span>. Also, the frequency response is exactly zero at <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega=\\pm 0.5\\pi"/> and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega =\\pi"/> since the zeros of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> are at these angles and lie on the unit circle.','8','12','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6172,'paragraph',NULL,'\figurepsAlt{Chapter08/art/8f26}{Chapter08/art/Hw_ellip}% {Frequency response (magnitude and phase) for a third-order IIR filter.  }','8','12','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6173,'math',NULL,'\\hat\\omega =0',8,12,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6174,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>      From <a href="eq:dirform" class="ITS_reference">&raquo;</a> or <a href="eq:factoredform" class="ITS_reference">&raquo;</a>, determine the value of the frequency response at <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega =0"/>. </div>','8','12','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6175,'math',NULL,'n',8,12,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6176,'math',NULL,'\\pm 0.3\\pi',8,12,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6177,'math',NULL,'(0.846)^n',8,12,7);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6178,'paragraph',NULL,'Finally, Fig. <a href="fig:iirexample-3" class="ITS_reference">&raquo;</a> shows the impulse response of the system.  Note that it oscillates and dies out with increasing <span class="ITS_HTML">n</span> because of the two complex conjugate poles at angles <span class="ITS_HTML">&plusmn; 0.3&pi;</span> and radius 0.846. The decaying envelope is <span class="ITS_HTML">(0.846)<sup>n</sup></span>.','8','12','7');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6179,'paragraph',NULL,'\figurepsAlt{Chapter08/art/8f27}{Chapter08/art/iir_hn_ellip}% {Impulse response  for a third-order IIR filter.  }','8','12','8');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6180,'paragraph',NULL,'<div class="ITS_EXE"><div class="ITS_EXE_logo">Exercise:</div>      Use the partial fraction form <a href="eq:fractionform" class="ITS_reference">&raquo;</a> to determine an equation for the impulse response of the filter. <i> Hint:</i> Apply the inverse \ZT. </div>','8','12','9');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6181,'paragraph',NULL,'The elliptic filter example described in this section is a simple example of a practical IIR lowpass filter.  Higher-order filters can exhibit much better frequency-selective filter characteristics.','8','12','10');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6182,'math',NULL,'H(z)',8,13,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6183,'section','sec:Ahead-ZZZZ-231','Summary and Links',8,13,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6184,'section','sec:Ahead-ZZZZ-231','Summary and Links',8,13,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6185,'paragraph',NULL,'<h3 class="ITS_BOOK">8-12  Summary and Links</h3> The class of IIR filters was introduced in this chapter, along with the \ZT\ method for filters with poles. The \ZT\ changes problems about impulse responses, frequency responses, and system structures into  algebraic manipulations of polynomials and rational functions. Poles of the system function <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?H(z)"/> turn out to be the most important elements for IIR filters because properties such as the shape of the frequency response or the form of the impulse response can be inferred quickly from the pole locations.','8','13','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6186,'math',NULL,'n',8,13,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6187,'math',NULL,'\\hat\\omega',8,13,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6188,'math',NULL,'z',8,13,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6189,'math',NULL,'(z)',8,13,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6190,'math',NULL,'(n)',8,13,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6191,'paragraph',NULL,'We also continued to stress the important concept of &ldquo;domains of representation.&rdquo; The <span class="ITS_HTML">n</span>-domain or time domain, the <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/>-domain or frequency domain, and the <span class="ITS_HTML">z</span>-domain give us  three domains for thinking about the characteristics of a system. We completed the ties between domains by introducing the inverse \ZT\ for constructing a signal from its \ZT. As a result, even difficult problems such as convolution can be simplified by working in the most convenient domain <span class="ITS_HTML">(z)</span> and then transforming back to the original domain <span class="ITS_HTML">(n)</span>.','8','13','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6192,'math',NULL,'z',8,13,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6193,'math',NULL,'n',8,13,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6194,'math',NULL,'\\hat\\omega',8,13,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6195,'paragraph',NULL,'Lab #11 is devoted to IIR filters. This lab uses a MATLAB  user interface tool called PeZ that supports an interactive exploration of the three domains. <h3 class="ITS_NOTE"><b>LAB:</b> #11 PeZ,  the <span class="ITS_HTML">z</span>, <span class="ITS_HTML">n</span>, and <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\hat\\omega"/> domains</h3> The PeZ tool has the potential for widespread use because it presents the user with multiple views of an LTI system: pole-zero domain, frequency response and impulse response. Similar capabilities are now being incorporated into many commercial software packages, e.g., <tt>sptool</tt> in MATLAB.','8','13','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6196,'math',NULL,'z',8,13,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6197,'math',NULL,'z',8,13,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6198,'paragraph',NULL,'The CD-ROM also contains the following demonstrations of the relationship between the <span class="ITS_HTML">z</span>-plane and the frequency domain and time domain: <ol class="ITS_alph"> <li> A set of &ldquo;three-domain&rdquo; movies that show how the frequency response and impulse response of an IIR filter change as a pole location is varied. Several different filters are demonstrated. <h3 class="ITS_NOTE"><b>DEMO:</b> Three-Domains IIR</h3> <li> A movie that  animates the relationship between the <span class="ITS_HTML">z</span>-plane and the unit circle where the frequency response lies. <h3 class="ITS_NOTE"><b>DEMO:</b> Z to Freq</h3> <li> The <tt>PeZ</tt> GUI can be used to construct different IIR and FIR filters. <h3 class="ITS_NOTE"><b>DEMO:</b> PeZ GUI</h3> <li> A tutorial on how to use <tt>PeZ.</tt> <h3 class="ITS_NOTE"><b>DEMO:</b> PeZ Tutorial</h3> <li> A demo that gives more examples of IIR filters. <h3 class="ITS_NOTE"><b>DEMO:</b> IIR Filtering</h3> </ol> The reader is again reminded of the large number of solved homework problems on the CD-ROM that are available for review and practice.','8','13','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6199,'paragraph',NULL,'<h3 class="ITS_NOTE"><b>NOTE:</b> Hundreds of Solved Problems</h3>','8','13','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6200,'chapter',NULL,'Continuous-Time Signals and LTI Systems',9,1,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6201,'chapter',NULL,'Continuous-Time Signals and LTI Systems',9,1,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6202,'paragraph',NULL,'<h2 class="ITS_BOOK">CHAPTER 9<p>Continuous-Time Signals and LTI Systems</h2>  We began this text with an introduction to continuous-time signals and systems, and now we are going to return to that topic. In Chapters <a href="chapter:four-sampling" class="ITS_reference">&raquo;</a>&mdash;<a href="chapter:eight-IIR" class="ITS_reference">&raquo;</a>, we focused on discrete-time signals and systems with topics such as sampling, FIR filtering, frequency response, and the \ZT. While discrete-time systems have many virtues, it is generally impossible to avoid some consideration of continuous-time signals and systems since most signals originate in continuous-time form. \iffalse This is because many systems are not based upon digital computation. For example, for physical systems such as the tuning fork,  time is &ldquo;real&rdquo; time; i.e., the continuously varying time that we assume governs our universe.  Such systems and the signals that they generate and process are most naturally represented by continuous  rather than discrete mathematics. \fi','9','1','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6203,'math',NULL,'x(t)',9,1,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6204,'math',NULL,'y(t)',9,1,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6205,'math',NULL,'x(t)',9,1,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6206,'math',NULL,'y(t)',9,1,2);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6207,'paragraph',NULL,'For example, musical instruments produce signals in continuous-time, and microphones convert the resulting acoustic pressure wave into a continuously varying electrical signal that can be further processed by amplifiers and filters. When stored on a CD-ROM, a music signal is converted to a discrete-time signal by sampling. However, when we listen to a music signal that is represented on a CD in digital form, it is necessary to convert the samples back to a continuous-time signal that can be processed by the human ear, another continuous-time system. Even when we want to perform analog processing using the combination of A/D converter, digital filter, and D/A converter as in Fig. <a href="fig:ct-dt-sys" class="ITS_reference">&raquo;</a> on p. <a href="fig:ct-dt-sys" class="ITS_reference">&raquo;</a>, the input is a continuous-time signal <span class="ITS_HTML">x(t)</span> and the output is another continuous-time signal, <span class="ITS_HTML">y(t)</span>. Since the overall effect of the system is a transformation from <span class="ITS_HTML">x(t)</span> to <span class="ITS_HTML">y(t)</span>,  we want to be able to describe and analyze that transformation independent of the fact that we might use a digital filter as part of the implementation.','9','1','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6208,'index',NULL,'Analog Signal Processing',9,1,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6209,'paragraph',NULL,'Another issue is the specification of processing systems in terms of their <i> ideal</i> behavior.  We will see that communication systems such as  radios  and  modems can be built up from ideal subsystems such as ideal filters and modulators.  The advantage of this abstract approach is that we can analyze the system behavior prior to specifying the implementation details of the subsystems. One might call this approach  <span class="ITS_blue">Analog Signal Processing</span>. In some cases, the preferred implementation of the continuous-time system might involve an analog electronic circuit; in others, a digital implementation would be employed.','9','1','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6210,'paragraph',NULL,'Therefore, it is useful to develop a good set of tools for analyzing continuous-time signals and systems, especially in the frequency-domain. The <i> Fourier Transform</i> provides the general method for describing the frequency content of continuous-time signals, as well as the frequency response of continuous-time systems. It also sheds new light on the relation between continuous-time and discrete-time signals and systems. This is the goal of Chapters 9&mdash;12.','9','1','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6211,'math',NULL,'\\delta(t)',9,1,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6212,'math',NULL,'\\delta(t)',9,1,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6213,'paragraph',NULL,'In this chapter, we will begin with a review of our earlier discussion of continuous-time sinusoidal signals and then introduce several other basic signals that are important in the study of continuous-time systems.  We will review the concepts of linearity and time-invariance and introduce the convolution representation of linear time-invariant systems.  As part of our discussion, we will introduce the unit-impulse signal <span class="ITS_HTML">&delta;(t)</span> and its mathematical properties.  This signal <span class="ITS_HTML">&delta;(t)</span> is necessary to define the impulse response for LTI systems, which is one element of continuous-time convolution.','9','1','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6214,'paragraph',NULL,'This chapter, along with Chapters 10 and 11, focuses on the basic theory of continuous-time signals and LTI systems. While it may appear that the results are highly mathematical, and it may not be obvious why they are useful, it is necessary to first build a foundation that can represent and solve real problems. Chapters 12 and 13 discuss a variety of applications of the basic concepts, including filtering, modulation, sampling, and spectrum analysis. Patient study of the fundamentals will be worth the effort!','9','1','6');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6215,'math',NULL,'x(t)',9,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6216,'math',NULL,'t',9,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6217,'section','sec:cont-time-signals','Continuous-Time Signals',9,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6218,'section','sec:cont-time-signals','Continuous-Time Signals',9,2,1);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6219,'paragraph',NULL,'<h3 class="ITS_BOOK">9-1  Continuous-Time Signals</h3> Continuous-time signals can be expressed as functions of a continuous independent variable.  We use the notation <span class="ITS_HTML">x(t)</span> to denote a continuous-time signal. The independent variable, <span class="ITS_HTML">t</span>, is generally associated with time, but that need not always be the case.  For example, the theory and techniques that we develop are readily applicable to other situations such as image processing where the independent variable is distance.','9','2','1');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6220,'paragraph',NULL,'It is useful to classify continuous-time signals in terms of their duration, or length. For our purposes, we will define the signal length to be the smallest time interval that contains all the nonzero signal values. In mathematical terminology, this interval is called the <span class="ITS_blue">support</span> of the signal.','9','2','2');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6221,'equation','eq:cosine-wave','x(t)= A\\cos(\\omega_0 t +\\phi)',9,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6222,'equation','eq:comp-exp','\\begin{align} z(t) &= X e^{j\\omega_0 t}= Ae^{j\\phi} e^{j\\omega_0 t}\\\\ &= A\\cos(\\omega_0 t +\\phi) + jA \\sin(\\omega_0 t +\\phi) \\end{align}',9,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6223,'math',NULL,'X',9,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6224,'math',NULL,'Ae^{j\\phi}',9,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6225,'subsection',NULL,'Two-Sided Infinite-Length Signals',9,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6226,'subsection',NULL,'Two-Sided Infinite-Length Signals',9,2,3);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6227,'paragraph',NULL,'<h3 class="ITS_BOOK">9-0.1  Two-Sided Infinite-Length Signals</h3> In Chapters 2 and 3, we studied various types of infinite length signals that were also periodic signals. We emphasized sinusoidal signals of the form <div class="ITS_EQUATION">x(t)= Acos(&omega;<sub>0</sub> t +&phi;)</div> which can also be represented as the real part of the complex exponential signal <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\begin{align} z(t) &= X e^{j\\omega_0 t}= Ae^{j\\phi} e^{j\\omega_0 t}\\\\ &= A\\cos(\\omega_0 t +\\phi) + jA \\sin(\\omega_0 t +\\phi) \\end{align}"/></div> where  the complex amplitude <span class="ITS_HTML">X</span> is equal to <span class="ITS_HTML">Ae<sup>j&phi;</sup></span>. In both <a href="eq:cosine-wave" class="ITS_reference">&raquo;</a> and <a href="eq:comp-exp" class="ITS_reference">&raquo;</a>, the signal is represented by an equation that gives a rule for assigning a signal value to each time value in the range \tinfty. Another common representation of these signals is a graph such as Fig. <a href="fig:CT_periodic_sigs" class="ITS_reference">&raquo;</a>(a).','9','2','3');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6228,'math',NULL,'1.5',9,2,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6229,'math',NULL,'\\omega_0 = 3\\pi',9,2,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6230,'math',NULL,'A=10',9,2,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6231,'math',NULL,'\\phi=0',9,2,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6232,'math',NULL,'3',9,2,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6233,'paragraph',NULL,'\figurepsAltScale{0.9}{Chapter09/art/CT_periodic_sigs}% {Continuous-time signals that are infinitely long: (a) <span class="ITS_HTML">1.5</span>-Hz Sinusoid with <span class="ITS_HTML">&omega;<sub>0</sub> = 3&pi;</span> rad/s, <span class="ITS_HTML">A=10</span>  and <span class="ITS_HTML">&phi;=0</span>. (b) Square wave with a fundamental frequency of <span class="ITS_HTML">3</span> Hz. (c) Two-sided decaying exponential signal.  }','9','2','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6234,'math',NULL,'x(t) = x(t+T_0)',9,2,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6235,'math',NULL,'T_0',9,2,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6236,'math',NULL,'T_0 = \\frac{1}{3}',9,2,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6237,'math',NULL,'x(t) =  5 e^{-|t|}',9,2,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6238,'paragraph',NULL,'A wider class of infinite-length signals is the class of periodic signals that satisfy the relationship <span class="ITS_HTML">x(t) = x(t+T<sub>0</sub>)</span>, where <span class="ITS_HTML">T<sub>0</sub></span> is the repetition period.  One common example of a periodic signal is the square wave shown in Fig. <a href="fig:CT_periodic_sigs" class="ITS_reference">&raquo;</a>(b) where the period is <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?T_0 = \\frac{1}{3}"/> sec.  Finally, it is possible to have an infinite length signal that is not periodic. An example is the decaying exponential signal <span class="ITS_HTML">x(t) =  5 e<sup>-|t|</sup></span> which has nonzero values over all time, \tinfty, as shown in Fig. <a href="fig:CT_periodic_sigs" class="ITS_reference">&raquo;</a>(c). The signals in Fig. <a href="fig:CT_periodic_sigs" class="ITS_reference">&raquo;</a> are often called <font color="blue">two-sided signals</font> because they are nonzero for both positive and negative time.','9','2','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6239,'equation','eq:unit_step','u(t)= \\begin{cases} 1 & \\quad  t \\geq 0 \\\\ 0 & \\quad  t<0 \\end{cases}',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6240,'equation','eq:turned-on-sinusoid','x(t)=10\\cos(3\\pi t - \\pi/4)u(t)',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6241,'equation','eq:exponential','v(t) = 5e^{-at}u(t)= \\begin{cases} 5e^{-at} & \\quad  t \\geq 0 \\\\ 0 & \\quad  t<0 \\end{cases}',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6242,'math',NULL,'x(t) = 0',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6243,'math',NULL,'t<t_0',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6244,'math',NULL,'t=t_0',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6245,'math',NULL,'t=0',9,2,6);
