INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6247,'math',NULL,'u(0)=\\frac{1}{2}',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6248,'math',NULL,'u(0)=1',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6249,'math',NULL,'u(0)',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6250,'math',NULL,'t=0',9,2,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6251,'math',NULL,'t < 0',9,2,6);
