INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6315,'equation','eq:zero','\\begin{eqnarray} & \\delta(t)=0\\quad   \\mbox{for <span class="ITS_HTML">t&ne; 0</span>}&  \\\\ & { \\int_{-\\infty}^{\\infty} \\delta(\\ttau) d\\ttau = 1}& LABEL#eq:area#LABEL \\end{eqnarray}',9,3,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6316,'footnote',NULL,'The unit area property assumes that the following delicate mathematical rearrangement is true <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\int\\!\\!\\lim\\limits_{\\Delta\\to 0',9,3,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6317,'footnote',NULL,'The unit area property assumes that the following delicate mathematical rearrangement is true <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\int\\!\\!\\lim\\limits_{\\Delta\\to 0',9,3,4);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6318,'paragraph',NULL,'Since it is problematic to define a function  of a continuous variable to be nonzero at only one isolated point, we must take an indirect approach and define the impulse to be the limit of a sequence of continuous functions that become increasingly concentrated at one point. An example is <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\delta_{\\Delta}(t) = \\begin{cases} \\frac{1}{2\\Delta} & \\quad  -\\Delta < t < \\Delta\\\\ 0 & \\quad  \\text{otherwise} \\end{cases}"/></div> which is a pulse whose width is <span class="ITS_HTML">2&Delta;</span> and whose height is <span class="ITS_HTML">1/(2&Delta;)</span>. As the  parameter  <span class="ITS_HTML">&Delta;</span> approaches zero, the pulse gets narrower, but higher. Figure <a href="fig:impulse" class="ITS_reference">&raquo;</a> \figurepsAltScale{0.9}{Chapter09/art/impulse_approx} {Approximating the unit impulse with narrowing pulses.  } shows examples of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\delta_{\\Delta}(t)"/> for three different values of <span class="ITS_HTML">&Delta;</span>. Each of these signals has the property that its total area is one, independent of <span class="ITS_HTML">&Delta;</span>, so we have <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\int_{-\\infty}^{\\infty} \\delta_{\\Delta}(\\tau) d\\tau = 1"/></div> The limit of the function <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\delta_{\\Delta}(t)"/> as <span class="ITS_HTML">&Delta;&rarr; 0</span> becomes zero everywhere except at <span class="ITS_HTML">t=0</span> where its value approaches <span class="ITS_HTML">&infin;</span>. We use this as an intuitive definition of the <i> unit impulse signal</i> <div class="ITS_EQUATION"><img class="beq" src="/cgi-bin/mimetex.cgi?\\delta(t) = \\lim\\limits_{\\Delta\\to 0} \\delta_\\Delta (t)"/></div> More important than the limiting value is the fact that the total area of <img class="ITS_TEX" src="/cgi-bin/mimetex.cgi?\\delta_{\\Delta}(t)"/> remains constant at one regardless of the size of <span class="ITS_HTML">&Delta;</span>.  This unit area property also applies<sup><a href="#" class="ITS_footnote">[note]</a></sup> \\delta_\\Delta (t) dt = \\lim\\limits_{\\Delta\\to 0}\\int \\delta_\\Delta (t) dt "/>.} to the limit <a href="eq:lim_impulse" class="ITS_reference">&raquo;</a>, so  we conclude that the <font color="blue">unit-impulse signal</font> has the following basic properties: <div class="ITS_EQUATION2"><img class="ITS_EQUATION" src="/cgi-bin/mimetex.cgi?\\begin{eqnarray} & \\delta(t)=0\\quad   \for <span class="ITS_HTML">t&ne; 0</span>&  \\\\ & { \\int_{-\\infty}^{\\infty} \\delta(\\ttau) d\\ttau = 1}&  \\end{eqnarray}"/></div>','9','3','4');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6319,'equation','none',' \\int_{-\\infty}^{\\infty} A\\delta(\\ttau) d\\ttau =A\\int_{-\\infty}^{\\infty} \\delta(\\ttau) d\\ttau =A ',9,3,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6320,'math',NULL,'\\delta(t)',9,3,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6321,'math',NULL,'(A)',9,3,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6322,'math',NULL,'A\\delta(t)',9,3,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6323,'math',NULL,'A',9,3,5);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6324,'paragraph',NULL,'In plotting the unit-impulse signal, we will use the style illustrated in Fig. <a href="fig:impulse_graph" class="ITS_reference">&raquo;</a>, which displays <span class="ITS_HTML">&delta;(t)</span> as a vertical line with an arrowhead on top. The area (or &ldquo;size&rdquo;)  of the impulse  <a href="eq:area" class="ITS_reference">&raquo;</a>  is written as the value <span class="ITS_HTML">(A)</span> in parentheses next to the arrowhead. \figurepsAltScale{0.9}{Chapter09/art/impulse_graph} {Unit-impulse signal is symbolized with an arrow. The area of the impulse is written in parentheses next to the arrowhead.  } If we write <span class="ITS_HTML">A&delta;(t)</span>, we say that the area or size of this scaled impulse signal is <span class="ITS_HTML">A</span> because <div class="ITS_EQUATION"><img class="bracket" src="/cgi-bin/mimetex.cgi? \\int_{-\\infty}^{\\infty} A\\delta(\\ttau) d\\ttau =A\\int_{-\\infty}^{\\infty} \\delta(\\ttau) d\\ttau =A "/></div>','9','3','5');
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6325,'equation','eq:imp_product','\\begin{align} f(t)\\delta(t) &= f(t) \\lim_{\\Delta\\to 0} \\delta_{\\Delta} (t)\\\\ &=  \\lim_{\\Delta\\to 0} f(t)\\delta_{\\Delta} (t)\\\\ &=  \\lim_{\\Delta\\to 0} f(0)\\delta_{\\Delta} (t) = f(0) \\delta(t) \\end{align}',9,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6326,'equation','none',' f(t)\\delta_{\\Delta}(t) =  \\begin{cases} f(t)/2\\Delta & \\quad   -\\Delta < t < \\Delta \\\\ 0 & \\quad  \\text{otherwise} \\end{cases} ',9,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6327,'math',NULL,'\\delta(t)',9,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6328,'math',NULL,'t=0',9,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6329,'math',NULL,'f(t)',9,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6330,'math',NULL,'\\delta_{\\Delta}(t)',9,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6331,'math',NULL,'f(t)',9,3,6);
INSERT INTO dspfirst (id,meta,name,content,chapter,section,paragraph) VALUES(6332,'math',NULL,'f(t)\\delta_{\\Delta}(t)',9,3,6);
